import React, { useState } from "react";
import { db } from "../firebase/firebaseConfig";
import { collection, addDoc, serverTimestamp } from "firebase/firestore";
import Swal from "sweetalert2";
import Sidebar from "../components/Sidebar";

function AddEvent() {
  const [title, setTitle] = useState("");
  const [location, setLocation] = useState("");
  const [date, setDate] = useState("");
  const [organizer, setOrganizer] = useState("");
  const [description, setDescription] = useState("");

  const handleAddEvent = async (e) => {
    e.preventDefault();

    if (!title || !location || !date || !organizer) {
      Swal.fire({
        icon: "warning",
        title: "Missing fields",
        text: "Please fill out all required fields.",
        timer: 2000,
        showConfirmButton: false,
      });
      return;
    }

    try {
      await addDoc(collection(db, "events"), {
        title,
        location,
        date,
        organizer,
        description,
        createdAt: serverTimestamp(),
      });

      Swal.fire({
        icon: "success",
        title: "Event Added!",
        text: "Your event has been successfully created.",
        timer: 2000,
        showConfirmButton: false,
      });

      // Reset form
      setTitle("");
      setLocation("");
      setDate("");
      setOrganizer("");
      setDescription("");
    } catch (error) {
      console.error("Error adding event:", error);
      Swal.fire({
        icon: "error",
        title: "Error",
        text: "Could not add event. Try again later.",
      });
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 flex items-center justify-center p-8">
        <div className="w-full max-w-2xl bg-white shadow-xl rounded-lg overflow-hidden">
          {/* Header */}
          <div className="bg-gradient-to-r from-blue-600 to-blue-500 p-4">
            <h2 className="text-2xl font-bold text-white font-cinzel">
              Add New Event
            </h2>
          </div>

          {/* Form */}
          <form onSubmit={handleAddEvent} className="p-6 space-y-4">
            {/* Title */}
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-1">
                Event Title <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Enter event title"
                className="w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {/* Location */}
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-1">
                Location <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                placeholder="Enter event location"
                className="w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {/* Date */}
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-1">
                Date <span className="text-red-500">*</span>
              </label>
              <input
                type="date"
                value={date}
                onChange={(e) => setDate(e.target.value)}
                className="w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {/* Organizer */}
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-1">
                Organizer <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                value={organizer}
                onChange={(e) => setOrganizer(e.target.value)}
                placeholder="Enter organizer name"
                className="w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {/* Description */}
            <div>
              <label className="block text-sm font-semibold text-gray-700 mb-1">
                Description (optional)
              </label>
              <textarea
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                placeholder="Write a short description about the event..."
                className="w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-500 resize-none h-24"
              />
            </div>

            {/* Submit */}
            <button
              type="submit"
              className="w-full bg-blue-600 text-white font-bold py-3 rounded-md hover:bg-blue-700 transition"
            >
              Add Event
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default AddEvent;
