import React, { useEffect, useMemo, useState } from "react";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  query,
  orderBy,
  onSnapshot,
  getDoc,
  doc,
  getDocs,
} from "firebase/firestore";

export default function Invalidated() {
  const [invalidatedUsers, setInvalidatedUsers] = useState([]);
  const [filtered, setFiltered] = useState([]);
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState("All");
  const [search, setSearch] = useState("");

  // ✅ Load Events For Dropdown
  useEffect(() => {
    async function loadEvents() {
      const snap = await getDocs(collection(db, "events"));
      const list = snap.docs.map((d) => ({
        id: d.id,
        title: d.data().title || "Untitled Event",
      }));
      setEvents(list);
    }
    loadEvents();
  }, []);

  // ✅ Real-time Invalidated Per Event
  useEffect(() => {
    const qRef = query(collection(db, "attendanceLogs"), orderBy("timestamp", "desc"));

    const unsub = onSnapshot(qRef, async (snap) => {
      const latestMap = new Map(); // event + student based

      for (const docSnap of snap.docs) {
        const data = docSnap.data();

        const studentID = data.studentID || "—";
        const eventId = data.eventId || "none";

        // UNIQUE PER EVENT
        const key = `${eventId}_${studentID}`;

        // Only take the latest entry PER EVENT PER STUDENT
        if (!latestMap.has(key)) {
          latestMap.set(key, { id: docSnap.id, ...data });
        }
      }

      // Filter invalidated only
      const invalidatedOnly = Array.from(latestMap.values()).filter(
        (d) => d.status !== "validated"
      );

      // Resolve Student IDs if missing
      const finalList = await Promise.all(
        invalidatedOnly.map(async (d) => {
          let studentID = d.studentID || "—";

          // If ID missing, fetch from users collection
          if ((!studentID || studentID === "—") && d.userId) {
            try {
              const userDoc = await getDoc(doc(db, "users", d.userId));
              if (userDoc.exists()) {
                studentID = userDoc.data().idNumber || "—";
              }
            } catch {
              studentID = "—";
            }
          }
          return { ...d, studentID };
        })
      );

      setInvalidatedUsers(finalList);
    });

    return () => unsub();
  }, []);

  // Format timestamp
  const fmtDate = (ts) => {
    if (!ts) return "—";
    const date = ts?.seconds ? new Date(ts.seconds * 1000) : new Date(ts);
    return date.toLocaleString("en-US", {
      month: "short",
      day: "numeric",
      year: "numeric",
      hour: "numeric",
      minute: "2-digit",
      hour12: true,
    });
  };

  // SEARCH + EVENT FILTER
  useEffect(() => {
    let results = invalidatedUsers;

    if (selectedEvent !== "All") {
      results = results.filter((u) => u.eventId === selectedEvent);
    }

    const term = search.toLowerCase();
    if (term) {
      results = results.filter(
        (u) =>
          u.studentID.toLowerCase().includes(term) ||
          (u.email || "—").toLowerCase().includes(term)
      );
    }

    setFiltered(results);
  }, [search, selectedEvent, invalidatedUsers]);

  const count = useMemo(() => filtered.length, [filtered]);

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">

        {/* HEADER */}
        <div className="flex items-center justify-between mb-8">
          <h1 className="text-3xl font-semibold text-gray-800">
            Invalidated Students
          </h1>

          <p className="text-red-600 font-semibold text-lg">
            Total: <span className="text-gray-800">{count}</span>
          </p>
        </div>

        {/* FILTER ROW */}
        <div className="flex flex-col md:flex-row md:items-center md:gap-4 mb-6">

          {/* EVENT DROPDOWN */}
          <select
            value={selectedEvent}
            onChange={(e) => setSelectedEvent(e.target.value)}
            className="px-4 py-3 rounded-lg border border-gray-300 bg-white text-gray-700 focus:ring-2 focus:ring-yellow-400 outline-none w-full md:w-1/3"
          >
            <option value="All">All Events</option>
            {events.map((ev) => (
              <option key={ev.id} value={ev.id}>
                {ev.title}
              </option>
            ))}
          </select>

          {/* SEARCH BAR */}
          <input
            type="text"
            placeholder="Search Student ID or Email..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="px-4 py-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-yellow-400 focus:outline-none w-full mt-4 md:mt-0"
          />
        </div>

        {/* TABLE */}
        <div className="bg-white rounded-xl shadow overflow-hidden">
          <table className="min-w-full table-auto border-collapse">
            <thead className="bg-gray-50 text-gray-700 text-sm font-semibold">
              <tr>
                <th className="px-6 py-3 text-left">Event</th>
                <th className="px-6 py-3 text-left">Student ID</th>
                <th className="px-6 py-3 text-left">Email</th>
                <th className="px-6 py-3 text-left">Last Updated</th>
                <th className="px-6 py-3 text-left">Status</th>
              </tr>
            </thead>

            <tbody>
              {filtered.length > 0 ? (
                filtered.map((user) => (
                  <tr key={user.id} className="border-t hover:bg-gray-50 transition">
                    <td className="px-6 py-3 text-sm text-gray-700">
                      {events.find((e) => e.id === user.eventId)?.title || "—"}
                    </td>
                    <td className="px-6 py-3 text-sm text-gray-700">
                      {user.studentID}
                    </td>
                    <td className="px-6 py-3 text-sm text-gray-700">
                      {user.email || "—"}
                    </td>
                    <td className="px-6 py-3 text-sm text-gray-700">
                      {fmtDate(user.timestamp)}
                    </td>
                    <td className="px-6 py-3">
                      <span className="px-3 py-1 text-xs font-semibold rounded-full bg-red-100 text-red-700">
                        INVALIDATED
                      </span>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="5"
                    className="px-6 py-10 text-center text-gray-500 italic"
                  >
                    No invalidated logs found for this event.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        <p className="text-xs text-gray-400 mt-4">
          * Shows only the latest invalidated status per student per event.
        </p>
      </div>
    </div>
  );
}
