import React, { useEffect, useState } from "react";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  addDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";
import Sidebar from "../components/Sidebar";
import Swal from "sweetalert2";

function ArchivedFeedbacks() {
  const [archivedFeedbacks, setArchivedFeedbacks] = useState([]);

  useEffect(() => {
    const unsubscribe = onSnapshot(
      collection(db, "archived_feedbacks"),
      (snapshot) => {
        const feedbackData = snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setArchivedFeedbacks(feedbackData);
      }
    );
    return () => unsubscribe();
  }, []);

  const handleRestore = async (id, feedbackData) => {
    const confirm = await Swal.fire({
      title: "Restore this feedback?",
      text: "This feedback will be moved back to active feedbacks.",
      icon: "question",
      showCancelButton: true,
      confirmButtonColor: "#1abc9c",
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Yes, restore it!",
    });

    if (confirm.isConfirmed) {
      try {
        await addDoc(collection(db, "feedbacks"), {
          ...feedbackData,
          restoredAt: new Date(),
        });
        await deleteDoc(doc(db, "archived_feedbacks", id));
        Swal.fire({
          icon: "success",
          title: "Restored!",
          text: "Feedback restored successfully.",
          timer: 2000,
          showConfirmButton: false,
        });
      } catch (err) {
        console.error("Error restoring feedback:", err);
        Swal.fire({
          icon: "error",
          title: "Failed to restore feedback",
          timer: 2000,
          showConfirmButton: false,
        });
      }
    }
  };

  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "Delete this feedback?",
      text: "This action cannot be undone.",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#e74c3c",
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Yes, delete it!",
    });

    if (confirm.isConfirmed) {
      try {
        await deleteDoc(doc(db, "archived_feedbacks", id));
        Swal.fire({
          icon: "success",
          title: "Deleted!",
          text: "Feedback permanently deleted.",
          timer: 2000,
          showConfirmButton: false,
        });
      } catch (err) {
        console.error("Error deleting feedback:", err);
        Swal.fire({
          icon: "error",
          title: "Failed to delete feedback",
          timer: 2000,
          showConfirmButton: false,
        });
      }
    }
  };

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />
      <div className="flex-1 p-10 overflow-y-auto">
        <h2 className="text-3xl font-semibold text-gray-800 mb-8">
          Archived Feedbacks
        </h2>

        <div className="bg-white shadow-lg rounded-xl overflow-hidden">
          <table className="min-w-full text-left text-gray-700">
            <thead className="bg-blue-600 text-white">
              <tr>
                <th className="px-6 py-3 text-sm font-medium">Event</th>
                <th className="px-6 py-3 text-sm font-medium">Rating</th>
                <th className="px-6 py-3 text-sm font-medium">Comment</th>
                <th className="px-6 py-3 text-sm font-medium text-center">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody>
              {archivedFeedbacks.length > 0 ? (
                archivedFeedbacks.map((fb) => (
                  <tr
                    key={fb.id}
                    className="border-b hover:bg-gray-50 transition"
                  >
                    <td className="px-6 py-4">{fb.eventName}</td>
                    <td className="px-6 py-4">{fb.rating} ★</td>
                    <td className="px-6 py-4">{fb.comment}</td>
                    <td className="px-6 py-4 flex gap-2 justify-center">
                      <button
                        className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded-md text-sm"
                        onClick={() => handleRestore(fb.id, fb)}
                      >
                        Restore
                      </button>
                      <button
                        className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded-md text-sm"
                        onClick={() => handleDelete(fb.id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="4"
                    className="px-6 py-8 text-center text-gray-500"
                  >
                    No archived feedbacks available.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default ArchivedFeedbacks;
