import React, { useState } from "react";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  doc,
  setDoc,
  serverTimestamp,
} from "firebase/firestore";
import Sidebar from "../components/Sidebar";
import Swal from "sweetalert2";

function AddEvent() {
  const [title, setTitle] = useState("");
  const [location, setLocation] = useState("");
  const [latitude, setLatitude] = useState("");
  const [longitude, setLongitude] = useState("");
  const [radius, setRadius] = useState("");
  const [date, setDate] = useState("");
  const [description, setDescription] = useState("");

  const handleAddEvent = async (e) => {
    e.preventDefault();

    if (!title || !location || !latitude || !longitude || !radius || !date) {
      Swal.fire({
        icon: "warning",
        title: "Missing Fields",
        text: "Please fill in all required fields before submitting.",
        position: "top-end",
        showConfirmButton: false,
        timer: 1500,
      });
      return;
    }

    try {
      // ðŸ”¥ Create event document with a manual ID
      const eventRef = doc(collection(db, "events"));

      // ðŸ”¥ Create event
      await setDoc(eventRef, {
        title,
        location,
        latitude: parseFloat(latitude),
        longitude: parseFloat(longitude),
        radius: parseFloat(radius),
        date,
        description,
        feedbackOpen: false, // important default
        createdAt: serverTimestamp(),
      });

      // ðŸ”¥ Create matching event_questions entry using SAME ID
      await setDoc(doc(db, "event_questions", eventRef.id), {
        eventId: eventRef.id,
        questions: [],      // empty by default
        openEnded: [],      // empty by default
        createdAt: serverTimestamp(),
      });

      Swal.fire({
        icon: "success",
        title: "Event Added!",
        text: "Event and question template created successfully.",
        position: "top-end",
        showConfirmButton: false,
        timer: 1500,
      });

      // Reset fields
      setTitle("");
      setLocation("");
      setLatitude("");
      setLongitude("");
      setRadius("");
      setDate("");
      setDescription("");
      
    } catch (error) {
      Swal.fire({
        icon: "error",
        title: "Failed to add event",
        text: error.message,
        position: "top-end",
        showConfirmButton: false,
        timer: 1500,
      });
    }
  };

  return (
    <div className="flex h-screen bg-gray-50 font-poppins">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 flex items-center justify-center p-10 overflow-y-auto">
        <div className="bg-white rounded-xl shadow-lg p-8 w-full max-w-2xl border border-gray-200">
          <h2 className="text-3xl font-semibold text-gray-800 mb-6 text-center">
            Add New Event
          </h2>

          <form onSubmit={handleAddEvent} className="space-y-5">
            {/* Event Title */}
            <div>
              <label className="block text-sm text-gray-600 mb-1">
                Event Title
              </label>
              <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-yellow-500 focus:border-yellow-500"
                placeholder="Enter event title"
                required
              />
            </div>

            {/* Event Location */}
            <div>
              <label className="block text-sm text-gray-600 mb-1">
                Event Location
              </label>
              <input
                type="text"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-yellow-500 focus:border-yellow-500"
                placeholder="Enter event location"
                required
              />
            </div>

            {/* Latitude & Longitude */}
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="block text-sm text-gray-600 mb-1">
                  Latitude
                </label>
                <input
                  type="number"
                  step="any"
                  value={latitude}
                  onChange={(e) => setLatitude(e.target.value)}
                  className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-yellow-500 focus:border-yellow-500"
                  placeholder="e.g. 8.508802"
                  required
                />
              </div>

              <div>
                <label className="block text-sm text-gray-600 mb-1">
                  Longitude
                </label>
                <input
                  type="number"
                  step="any"
                  value={longitude}
                  onChange={(e) => setLongitude(e.target.value)}
                  className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-yellow-500 focus:border-yellow-500"
                  placeholder="e.g. 124.602974"
                  required
                />
              </div>
            </div>

            {/* Radius */}
            <div>
              <label className="block text-sm text-gray-600 mb-1">
                Validation Radius (in meters)
              </label>
              <input
                type="number"
                value={radius}
                onChange={(e) => setRadius(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-yellow-500 focus:border-yellow-500"
                placeholder="e.g. 50"
                required
              />
            </div>

            {/* Date */}
            <div>
              <label className="block text-sm text-gray-600 mb-1">Date</label>
              <input
                type="date"
                value={date}
                onChange={(e) => setDate(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-yellow-500 focus:border-yellow-500"
                required
              />
            </div>

            {/* Description */}
            <div>
              <label className="block text-sm text-gray-600 mb-1">
                Description
              </label>
              <textarea
                value={description}
                onChange={(e) => setDescription(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-yellow-500 focus:border-yellow-500"
                rows="4"
                placeholder="Enter event details..."
              ></textarea>
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              className="w-full bg-gray-700 text-white py-3 rounded-md font-semibold hover:bg-gray-600 transition"
            >
              Add Event
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default AddEvent;
