// src/pages/AddNotifications.js
import React, { useEffect, useMemo, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  doc,
  getDoc,
  updateDoc,
  setDoc,
  arrayUnion,
} from "firebase/firestore";

import {
  getStorage,
  ref,
  uploadBytes,
  getDownloadURL,
} from "firebase/storage";

export default function AddNotifications() {
  const navigate = useNavigate();
  const storage = getStorage();

  const [events, setEvents] = useState([]);
  const [selectedId, setSelectedId] = useState("");
  const [selectedDoc, setSelectedDoc] = useState(null);

  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");

  const [imageFile, setImageFile] = useState(null);

  // modal state
  const [modal, setModal] = useState({
    open: false,
    type: "",
    message: "",
  });

  // ðŸ”¥ REAL FIX: store confirm callback in a ref
  const confirmCallback = useRef(null);

  const openModal = (type, message, onConfirm = null) => {
    confirmCallback.current = onConfirm; // store safely
    setModal({ open: true, type, message });
  };

  const closeModal = () => {
    confirmCallback.current = null;
    setModal({ open: false, type: "", message: "" });
  };

  const notifications = useMemo(() => {
    const arr = Array.isArray(selectedDoc?.notifications)
      ? selectedDoc.notifications
      : [];

    return [...arr].sort((a, b) => {
      const ta = a?.createdAt ? new Date(a.createdAt).getTime() : 0;
      const tb = b?.createdAt ? new Date(b.createdAt).getTime() : 0;
      return tb - ta;
    });
  }, [selectedDoc]);

  useEffect(() => {
    const unsub = onSnapshot(collection(db, "events"), (snap) => {
      const list = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
      list.sort((a, b) => (b.isCurrent === true) - (a.isCurrent === true));
      setEvents(list);

      if (!selectedId) {
        const current = list.find((e) => e.isCurrent);
        setSelectedId(current ? current.id : list[0]?.id || "");
      }
    });
    return () => unsub();
  }, [selectedId]);

  useEffect(() => {
    if (!selectedId) return;
    const ref = doc(db, "events", selectedId);
    const unsub = onSnapshot(ref, (snap) =>
      setSelectedDoc({ id: snap.id, ...snap.data() })
    );
    return () => unsub();
  }, [selectedId]);

  const uploadImage = async () => {
    if (!imageFile) return null;

    const filePath = `notifications/${selectedId}/${Date.now()}-${imageFile.name}`;
    const fileRef = ref(storage, filePath);

    await uploadBytes(fileRef, imageFile);
    return await getDownloadURL(fileRef);
  };

  const confirmAdd = async () => {
    if (!selectedId) return;

    let imageURL = null;

    if (imageFile) {
      imageURL = await uploadImage();
    }

    const refEvent = doc(db, "events", selectedId);

    const payload = {
      title: title.trim() || undefined,
      body: body.trim(),
      createdAt: new Date().toISOString(),
      imageURL,
    };

    try {
      await updateDoc(refEvent, { notifications: arrayUnion(payload) });
    } catch {
      const snap = await getDoc(refEvent);
      const arr = Array.isArray(snap.data()?.notifications)
        ? snap.data().notifications
        : [];

      await setDoc(
        refEvent,
        { notifications: [...arr, payload] },
        { merge: true }
      );
    }

    setTitle("");
    setBody("");
    setImageFile(null);

    closeModal();
  };

  const handleAdd = (e) => {
    e.preventDefault();

    if (!selectedId) {
      openModal("warning", "Select an event first.");
      return;
    }

    if (!title.trim() && !body.trim() && !imageFile) {
      openModal("warning", "Please enter a message or attach an image.");
      return;
    }

    openModal("confirm", "Add this notification?", confirmAdd);
  };

  const confirmDelete = async (index) => {
    const refEvent = doc(db, "events", selectedId);
    const snap = await getDoc(refEvent);
    const arr = Array.isArray(snap.data()?.notifications)
      ? snap.data().notifications
      : [];

    const next = arr.filter((_, i) => i !== index);

    await updateDoc(refEvent, { notifications: next });

    closeModal();
  };

  const handleDelete = (i) =>
    openModal("delete", "Delete this notification?", () =>
      confirmDelete(i)
    );

  const confirmClearAll = async () => {
    await updateDoc(doc(db, "events", selectedId), { notifications: [] });
    closeModal();
  };

  const handleClearAll = () => {
    if (!selectedId || notifications.length === 0) return;
    openModal("delete", "Clear ALL notifications?", confirmClearAll);
  };

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-8 overflow-y-auto">
        {/* header */}
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-3xl font-semibold text-gray-800">Add Notifications</h1>
          <button
            onClick={() => navigate("/admin/dashboard")}
            className="px-4 py-2 bg-white border rounded-md hover:bg-gray-100"
          >
            Back to Dashboard
          </button>
        </div>

        {/* event picker */}
        <div className="bg-white rounded-xl shadow p-5 mb-5">
          <label className="font-medium text-sm text-gray-700 mb-2 block">
            Select Event{" "}
            {selectedDoc?.isCurrent && (
              <span className="text-xs text-green-600 ml-2 font-semibold">
                CURRENT
              </span>
            )}
          </label>

          <select
            value={selectedId}
            onChange={(e) => setSelectedId(e.target.value)}
            className="w-full border px-3 py-2 rounded-md"
          >
            {events.map((ev) => (
              <option key={ev.id} value={ev.id}>
                {ev.title} {ev.isCurrent ? "â€¢ (Current)" : ""}
              </option>
            ))}
          </select>
        </div>

        {/* form */}
        <form onSubmit={handleAdd} className="bg-white rounded-xl shadow p-5">
          <h2 className="text-lg font-semibold text-gray-800 mb-4">Create Notification</h2>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
            <input
              type="text"
              value={title}
              placeholder="Subject (optional)"
              onChange={(e) => setTitle(e.target.value)}
              className="border rounded-md px-3 py-2"
            />

            <input
              type="text"
              value={body}
              placeholder="Message"
              onChange={(e) => setBody(e.target.value)}
              className="border rounded-md px-3 py-2 md:col-span-2"
            />
          </div>

          <div className="mt-4 pr-4">
            <label className="text-sm font-medium text-gray-700">
              Optional Image Attachment
            </label>

            <input
              type="file"
              accept="image/*"
              onChange={(e) => setImageFile(e.target.files[0] || null)}
              className="mt-1 cursor-pointer"
            />

            {imageFile && (
              <p className="text-xs text-gray-500 mt-1">
                Selected: {imageFile.name}
              </p>
            )}
          </div>

          <div className="mt-4 flex gap-3">
            <button
              type="submit"
              className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700"
            >
              Add Notification
            </button>

            <button
              type="button"
              onClick={handleClearAll}
              disabled={notifications.length === 0}
              className="bg-gray-600 text-white px-4 py-2 rounded-md hover:bg-gray-700"
            >
              Clear All
            </button>
          </div>
        </form>

        {/* existing notifications */}
        <div className="bg-white rounded-xl shadow p-5 mt-5">
          <h2 className="text-lg font-semibold mb-3">Existing Notifications</h2>

          {notifications.length === 0 ? (
            <p className="text-gray-500">No notifications available.</p>
          ) : (
            <ul className="space-y-3">
              {notifications.map((n, i) => (
                <li key={i} className="border p-3 rounded-lg flex justify-between">
                  <div>
                    {n.title && <p className="font-semibold">{n.title}</p>}
                    <p>{n.body}</p>

                    {n.imageURL && (
                      <img
                        src={n.imageURL}
                        alt="attachment"
                        className="mt-2 w-32 h-32 object-cover border rounded-lg cursor-pointer"
                        onClick={() => window.open(n.imageURL, "_blank")}
                      />
                    )}

                    <p className="text-xs text-gray-400 mt-1">
                      {new Date(n.createdAt).toLocaleString()}
                    </p>
                  </div>

                  <button
                    onClick={() => handleDelete(i)}
                    className="bg-red-600 text-white px-3 py-1 rounded-md"
                  >
                    Delete
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>

      {/* modal */}
      {modal.open && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white rounded-xl shadow-xl w-96 p-6 text-center">
            <h3 className="text-xl font-semibold mb-4">
              {modal.type === "confirm"
                ? "Confirm Action"
                : modal.type === "delete"
                ? "Delete"
                : "Notice"}
            </h3>

            <p className="mb-6">{modal.message}</p>

            <div className="flex justify-center gap-4">
              {modal.type !== "warning" && (
                <button
                  onClick={() => confirmCallback.current && confirmCallback.current()}
                  className="bg-yellow-400 px-4 py-2 rounded-md hover:bg-yellow-500"
                >
                  Confirm
                </button>
              )}

              <button
                onClick={closeModal}
                className="bg-gray-300 px-4 py-2 rounded-md hover:bg-gray-400"
              >
                {modal.type === "warning" ? "Okay" : "Cancel"}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
