import React, { useEffect, useState, useMemo } from "react";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  query,
  where,
  onSnapshot,
  getDoc,
  doc,
  orderBy,
  limit,
} from "firebase/firestore";

export default function Invalidated() {
  const [invalidatedUsers, setInvalidatedUsers] = useState([]);
  const [search, setSearch] = useState("");
  const [filtered, setFiltered] = useState([]);
  const [currentEvent, setCurrentEvent] = useState(null);

  // ‚úÖ Fetch current event
  useEffect(() => {
    const qRef = query(collection(db, "events"), where("isCurrent", "==", true), limit(1));
    const unsub = onSnapshot(qRef, (snap) => {
      if (!snap.empty) {
        const event = snap.docs[0].data();
        setCurrentEvent(event);
      } else {
        setCurrentEvent(null);
      }
    });
    return () => unsub();
  }, []);

  // ‚úÖ Real-time fetch of invalidated users for current event (from attendanceLogs)
  useEffect(() => {
    if (!currentEvent?.title) return;

    const qRef = query(
      collection(db, "attendanceLogs"),
      where("status", "==", "invalidated"),
      where("eventTitle", "==", currentEvent.title),
      orderBy("timestamp", "desc")
    );

    const unsub = onSnapshot(qRef, async (snap) => {
      const list = await Promise.all(
        snap.docs.map(async (d) => {
          const data = d.data();
          let studentID = data.studentID || "‚Äî";

          // üîç Fetch missing student ID
          if ((!studentID || studentID === "‚Äî") && data.userId) {
            try {
              const userDoc = await getDoc(doc(db, "users", data.userId));
              if (userDoc.exists()) {
                studentID = userDoc.data().idNumber || "‚Äî";
              }
            } catch {
              studentID = "‚Äî";
            }
          }

          return { id: d.id, ...data, studentID };
        })
      );
      setInvalidatedUsers(list);
    });

    return () => unsub();
  }, [currentEvent]);

  // ‚úÖ Format date nicely
  const fmtDate = (ts) => {
    if (!ts) return "‚Äî";
    const date = ts?.seconds ? new Date(ts.seconds * 1000) : new Date(ts);
    return date.toLocaleString("en-US", {
      month: "short",
      day: "numeric",
      year: "numeric",
      hour: "numeric",
      minute: "2-digit",
      hour12: true,
    });
  };

  // ‚úÖ Search filter
  useEffect(() => {
    const term = search.toLowerCase();
    let results = invalidatedUsers;

    if (term) {
      results = results.filter(
        (l) =>
          l.studentID?.toString().toLowerCase().includes(term) ||
          l.email?.toLowerCase().includes(term)
      );
    }

    setFiltered(results);
  }, [invalidatedUsers, search]);

  // ‚úÖ Count
  const count = useMemo(() => filtered.length, [filtered]);

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">
        {/* ‚úÖ Header */}
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-3xl font-semibold text-gray-800">
            Invalidated{" "}
            {currentEvent?.title && (
              <span className="text-yellow-500 font-semibold">
                ‚Äì ‚Äú{currentEvent.title}‚Äù
              </span>
            )}
          </h1>
          <p className="text-red-600 font-medium">
            Total Invalidated:{" "}
            <span className="text-gray-800 font-semibold">{count}</span>
          </p>
        </div>

        {/* ‚úÖ Search Bar */}
        <div className="flex mb-6">
          <input
            type="text"
            placeholder="Search by Student ID or Email..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-yellow-400 focus:outline-none"
          />
        </div>

        {/* ‚úÖ Table */}
        <div className="bg-white rounded-xl shadow overflow-hidden">
          <table className="min-w-full table-auto border-collapse">
            <thead className="bg-gray-50 text-gray-700 text-sm font-semibold">
              <tr>
                <th className="px-6 py-3 text-left">Student ID</th>
                <th className="px-6 py-3 text-left">Email</th>
                <th className="px-6 py-3 text-left">Date &amp; Time</th>
                <th className="px-6 py-3 text-left">Status</th>
              </tr>
            </thead>
            <tbody>
              {filtered.length > 0 ? (
                filtered.map((user) => (
                  <tr key={user.id} className="border-t hover:bg-gray-50 transition">
                    <td className="px-6 py-3 text-sm text-gray-700">{user.studentID}</td>
                    <td className="px-6 py-3 text-sm text-gray-700">{user.email || "‚Äî"}</td>
                    <td className="px-6 py-3 text-sm text-gray-700">{fmtDate(user.timestamp)}</td>
                    <td className="px-6 py-3">
                      <span className="px-3 py-1 text-xs font-semibold rounded-full bg-red-100 text-red-700">
                        INVALIDATED
                      </span>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="4" className="px-6 py-10 text-center text-gray-500 italic">
                    {currentEvent
                      ? "No invalidated users for this event yet."
                      : "No current event selected."}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        <p className="text-xs text-gray-400 mt-4">
          * This list updates live as users leave or lose validation from the event.
        </p>
      </div>
    </div>
  );
}
