import React, { useEffect, useMemo, useState } from "react";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import { collection, onSnapshot, query, orderBy } from "firebase/firestore";
import Swal from "sweetalert2";

export default function AttendanceLogs() {
  const [logs, setLogs] = useState([]);
  const [search, setSearch] = useState("");
  const [filtered, setFiltered] = useState([]);

  // Real-time fetch
  useEffect(() => {
    const qRef = query(collection(db, "checkins"), orderBy("createdAt", "desc"));
    const unsub = onSnapshot(qRef, (snap) => {
      const list = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
      setLogs(list);
    });
    return () => unsub();
  }, []);

  // Search filter
  useEffect(() => {
    const term = search.toLowerCase();
    setFiltered(
      logs.filter(
        (l) =>
          l.fullName?.toLowerCase().includes(term) ||
          l.email?.toLowerCase().includes(term) ||
          l.studentId?.toLowerCase().includes(term)
      )
    );
  }, [search, logs]);

  // Count
  const counts = useMemo(() => {
    const total = logs.length;
    const valid = logs.filter((l) => l.valid === true).length;
    const invalid = logs.filter((l) => l.valid === false).length;
    return { total, valid, invalid };
  }, [logs]);

  const fmtDate = (ts) => {
    try {
      const d = ts?.toDate?.() instanceof Date ? ts.toDate() : new Date(ts || Date.now());
      return d.toLocaleString();
    } catch {
      return "—";
    }
  };

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-3xl font-semibold text-gray-800">
            Attendance Logs
          </h1>
          <div className="text-right text-sm text-gray-600">
            <div>Total: <span className="font-semibold text-gray-800">{counts.total}</span></div>
            <div className="text-green-600">Validated: {counts.valid}</div>
            <div className="text-red-600">Invalidated: {counts.invalid}</div>
          </div>
        </div>

        {/* Search */}
        <div className="mb-6">
          <input
            type="text"
            placeholder="Search by Student ID, Name, or Email..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-yellow-400 focus:outline-none"
          />
        </div>

        {/* Table */}
        <div className="bg-white rounded-xl shadow overflow-hidden">
          <table className="min-w-full table-auto border-collapse">
            <thead className="bg-gray-50 text-gray-700 text-sm font-semibold">
              <tr>
                <th className="px-6 py-3 text-left">Student ID</th>
                <th className="px-6 py-3 text-left">Full Name</th>
                <th className="px-6 py-3 text-left">Email</th>
                <th className="px-6 py-3 text-left">Validation Status</th>
                <th className="px-6 py-3 text-left">Date</th>
              </tr>
            </thead>
            <tbody>
              {filtered.length > 0 ? (
                filtered.map((log) => (
                  <tr
                    key={log.id}
                    className="border-t hover:bg-gray-50 transition"
                  >
                    <td className="px-6 py-3 text-sm text-gray-700">
                      {log.studentId || "—"}
                    </td>
                    <td className="px-6 py-3 text-sm font-medium text-gray-800">
                      {log.fullName || "Unknown"}
                    </td>
                    <td className="px-6 py-3 text-sm text-gray-600">
                      {log.email || "—"}
                    </td>
                    <td className="px-6 py-3">
                      <span
                        className={`px-3 py-1 text-xs font-semibold rounded-full ${
                          log.valid
                            ? "bg-green-100 text-green-700"
                            : "bg-red-100 text-red-700"
                        }`}
                      >
                        {log.valid ? "Validated" : "Invalidated"}
                      </span>
                    </td>
                    <td className="px-6 py-3 text-xs text-gray-500">
                      {fmtDate(log.createdAt)}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="5"
                    className="px-6 py-10 text-center text-gray-500 italic"
                  >
                    No attendance logs found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        {/* Hint */}
        <p className="text-xs text-gray-400 mt-4">
          * This table updates in real-time. Use search to find specific students if there’s a
          validation issue.
        </p>
      </div>
    </div>
  );
}
