// src/admin/ArchivedEvents.js
import React, { useEffect, useState } from "react";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  addDoc,
  deleteDoc,
  doc,
  getDoc,
  setDoc,
  getDocs,
  query,
  where,
} from "firebase/firestore";
import Sidebar from "../components/Sidebar";
import { useNavigate } from "react-router-dom";

function ArchivedEvents() {
  const [archivedEvents, setArchivedEvents] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);

  const navigate = useNavigate();

  // Load archived events
  useEffect(() => {
    const unsub = onSnapshot(collection(db, "archived_events"), (snap) => {
      const list = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
      setArchivedEvents(list);
    });

    return () => unsub();
  }, []);

  const openRestoreModal = (event) => {
    setSelectedEvent(event);
    setShowModal(true);
  };

  /* ------------------------------------------------------------
      ⭐ FULL RESTORE PROCESS
      1. Restore event document
      2. Restore event_questions
      3. Restore feedbacks
      4. Restore archived_feedbacks → feedbacks
     ------------------------------------------------------------ */
  const handleRestore = async () => {
    if (!selectedEvent) return;

    try {
      const eventId = selectedEvent.originalId || selectedEvent.id;

      console.log("Restoring event:", eventId);

      /* ------------------ 1. RESTORE EVENT DOCUMENT ------------------ */
      await setDoc(doc(db, "events", eventId), {
        ...selectedEvent,
        originalId: null,
        archivedAt: null,
        isCurrent: false,
        restoredAt: new Date(),
      });

      /* ------------------ 2. RESTORE QUESTIONS ------------------ */
      if (selectedEvent.event_questions) {
        await setDoc(doc(db, "event_questions", eventId), {
          questions: selectedEvent.event_questions.questions || [],
          openEnded: selectedEvent.event_questions.openEnded || [],
          createdAt: selectedEvent.event_questions.createdAt || new Date(),
          updatedAt: new Date(),
        });
      }

      /* ------------------ 3. RESTORE FEEDBACKS ------------------ */
      const feedbackSnap = await getDocs(
        query(collection(db, "archived_event_feedbacks"), where("eventId", "==", eventId))
      );

      for (const fb of feedbackSnap.docs) {
        const fbData = fb.data();

        // move back to "feedbacks"
        await addDoc(collection(db, "feedbacks"), {
          ...fbData,
          restoredAt: new Date(),
        });

        // delete from archived feedbacks
        await deleteDoc(doc(db, "archived_event_feedbacks", fb.id));
      }

      /* ------------------ 4. ALTERNATIVE: restore archived_feedbacks (old version) ------------------ */
      const altSnap = await getDocs(
        query(collection(db, "archived_feedbacks"), where("eventId", "==", eventId))
      );

      for (const fb of altSnap.docs) {
        const fbData = fb.data();

        await addDoc(collection(db, "feedbacks"), {
          ...fbData,
          restoredAt: new Date(),
        });

        await deleteDoc(doc(db, "archived_feedbacks", fb.id));
      }

      /* ------------------ 5. DELETE ARCHIVED_EVENT ------------------ */
      await deleteDoc(doc(db, "archived_events", selectedEvent.id));

      setShowModal(false);
      setSelectedEvent(null);

      alert("Event restored successfully with full data!");
    } catch (e) {
      console.error("RESTORE ERROR:", e);
      alert("Failed to restore event.");
    }
  };

  /* -------------------- DELETE FROM ARCHIVES -------------------- */
  const handleDelete = async (id) => {
    try {
      await deleteDoc(doc(db, "archived_events", id));
    } catch (e) {
      console.error("Delete Error:", e);
    }
  };

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />
      <div className="flex-1 p-10 overflow-y-auto">
        <h2 className="text-3xl font-semibold text-gray-800 mb-8">
          Archived Events
        </h2>

        {/* Table */}
        <div className="bg-white shadow-xl rounded-2xl overflow-hidden border border-gray-200">
          <table className="min-w-full text-left">
            <thead className="bg-gray-800 text-gray-100">
              <tr>
                <th className="px-6 py-3">Title</th>
                <th className="px-6 py-3">Location</th>
                <th className="px-6 py-3">Date</th>
                <th className="px-6 py-3 text-center">Actions</th>
              </tr>
            </thead>

            <tbody>
              {archivedEvents.length > 0 ? (
                archivedEvents.map((ev) => (
                  <tr
                    key={ev.id}
                    className="border-b border-gray-200 hover:bg-gray-50"
                  >
                    <td className="px-6 py-4 text-gray-800">{ev.title}</td>
                    <td className="px-6 py-4 text-gray-700">{ev.location}</td>
                    <td className="px-6 py-4 text-gray-700">{ev.date}</td>

                    <td className="px-6 py-4">
                      <div className="flex justify-center gap-2">
                        <button
                          className="bg-gray-700 hover:bg-gray-900 text-white px-3 py-1 rounded-md text-sm"
                          onClick={() => openRestoreModal(ev)}
                        >
                          Restore
                        </button>

                        <button
                          className="bg-yellow-400 hover:bg-yellow-500 text-gray-900 px-3 py-1 rounded-md text-sm"
                          onClick={() => handleDelete(ev.id)}
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="4"
                    className="text-center px-6 py-8 text-gray-500 bg-white"
                  >
                    No archived events available.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Restore Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-xl shadow-xl w-96">
            <h3 className="text-xl font-semibold text-gray-800 mb-4">
              Restore Event
            </h3>
            <p className="text-gray-600 mb-6">
              Restore{" "}
              <span className="font-medium text-gray-900">
                “{selectedEvent?.title}”
              </span>{" "}
              back to active events?
            </p>

            <div className="flex justify-center gap-4">
              <button
                onClick={handleRestore}
                className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-md"
              >
                Restore
              </button>
              <button
                onClick={() => setShowModal(false)}
                className="bg-gray-300 hover:bg-gray-400 px-4 py-2 rounded-md"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ArchivedEvents;
