import React, { useEffect, useState } from "react";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  updateDoc,
  doc,
} from "firebase/firestore";
import Swal from "sweetalert2";
import Sidebar from "../components/Sidebar";

function ManageEvents() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, "events"), (snapshot) => {
      const eventsData = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setEvents(eventsData);
    });

    return () => unsubscribe();
  }, []);

  const handleSetCurrent = async (id) => {
    try {
      const promises = events.map((event) =>
        updateDoc(doc(db, "events", event.id), { isCurrent: event.id === id })
      );
      await Promise.all(promises);

      Swal.fire({
        icon: "success",
        title: "Current event updated!",
        background: "#1E293B",
        color: "#fff",
        timer: 2000,
        showConfirmButton: false,
      });
    } catch (error) {
      console.error("Error setting current event:", error);
      Swal.fire({
        icon: "error",
        title: "Failed to update event",
        background: "#1E293B",
        color: "#fff",
        timer: 2000,
        showConfirmButton: false,
      });
    }
  };

  const handleArchive = async (id) => {
    const confirm = await Swal.fire({
      title: "Archive this event?",
      text: "This event will be moved to Archived Events.",
      icon: "question",
      background: "#1E293B",
      color: "#fff",
      showCancelButton: true,
      confirmButtonColor: "#e74c3c",
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Yes, archive it!",
    });

    if (confirm.isConfirmed) {
      try {
        await updateDoc(doc(db, "events", id), { isArchived: true });
        Swal.fire({
          icon: "success",
          title: "Archived!",
          text: "Event moved to Archived Events.",
          background: "#1E293B",
          color: "#fff",
          timer: 2000,
          showConfirmButton: false,
        });
      } catch (error) {
        console.error("Error archiving event:", error);
        Swal.fire({
          icon: "error",
          title: "Failed to archive event",
          background: "#1E293B",
          color: "#fff",
          timer: 2000,
          showConfirmButton: false,
        });
      }
    }
  };

  const handleEdit = async (event) => {
    const {
      title = "",
      location = "",
      date = "",
      latitude = "",
      longitude = "",
      radius = "",
      description = "",
    } = event;

    const { value: formValues } = await Swal.fire({
      title: "Edit Event Details",
      background: "#1E293B",
      color: "#fff",
      html: `
        <div style="text-align:left; display:flex; flex-direction:column; gap:10px;">

          <label style="font-size:13px;">Title</label>
          <input id="title" class="swal2-input" style="width:100%;" placeholder="Event title" value="${title}">

          <label style="font-size:13px;">Location</label>
          <input id="location" class="swal2-input" style="width:100%;" placeholder="Event location" value="${location}">

          <label style="font-size:13px;">Date</label>
          <input id="date" type="date" class="swal2-input" style="width:100%;" value="${date}">

          <div style="display:flex; gap:10px;">
            <div style="flex:1;">
              <label style="font-size:13px;">Latitude</label>
              <input id="latitude" class="swal2-input" placeholder="Latitude" value="${latitude}">
            </div>
            <div style="flex:1;">
              <label style="font-size:13px;">Longitude</label>
              <input id="longitude" class="swal2-input" placeholder="Longitude" value="${longitude}">
            </div>
          </div>

          <label style="font-size:13px;">Radius (meters)</label>
          <input id="radius" type="number" class="swal2-input" placeholder="e.g. 50" value="${radius}">

          <label style="font-size:13px;">Description</label>
          <textarea id="description" class="swal2-textarea" placeholder="Event description" style="resize:none; height:80px;">${description}</textarea>
        </div>
      `,
      focusConfirm: false,
      confirmButtonText: "Save Changes",
      confirmButtonColor: "#facc15",
      showCancelButton: true,
      cancelButtonColor: "#475569",
      preConfirm: () => {
        return {
          title: document.getElementById("title").value,
          location: document.getElementById("location").value,
          date: document.getElementById("date").value,
          latitude: document.getElementById("latitude").value,
          longitude: document.getElementById("longitude").value,
          radius: document.getElementById("radius").value,
          description: document.getElementById("description").value,
        };
      },
    });

    if (formValues) {
      try {
        await updateDoc(doc(db, "events", event.id), formValues);
        Swal.fire({
          icon: "success",
          title: "Event updated successfully!",
          background: "#1E293B",
          color: "#fff",
          timer: 2000,
          showConfirmButton: false,
        });
      } catch (error) {
        console.error("Error updating event:", error);
        Swal.fire({
          icon: "error",
          title: "Failed to update event",
          background: "#1E293B",
          color: "#fff",
          timer: 2000,
          showConfirmButton: false,
        });
      }
    }
  };

  return (
    <div className="flex h-screen bg-gray-50 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">
        <h2 className="text-3xl font-semibold text-gray-800 mb-8">
          Manage Events
        </h2>

        <div className="bg-[#1E293B] shadow-lg rounded-xl overflow-hidden">
          <table className="min-w-full text-left text-gray-800">
            <thead className="bg-[#0F172A] text-white">
              <tr>
                <th className="px-6 py-3 text-sm font-medium">Title</th>
                <th className="px-6 py-3 text-sm font-medium">Location</th>
                <th className="px-6 py-3 text-sm font-medium">Date</th>
                <th className="px-6 py-3 text-sm font-medium text-center">
                  Current
                </th>
                <th className="px-6 py-3 text-sm font-medium text-center">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody>
              {events.length > 0 ? (
                events.map((event) => (
                  <tr
                    key={event.id}
                    className="border-b border-gray-300 hover:bg-gray-100 transition bg-white"
                  >
                    <td className="px-6 py-4">{event.title}</td>
                    <td className="px-6 py-4">{event.location}</td>
                    <td className="px-6 py-4">{event.date}</td>
                    <td className="px-6 py-4 text-center">
                      {event.isCurrent ? (
                        <span className="px-3 py-1 text-xs rounded-full bg-yellow-200 text-yellow-900 font-semibold">
                          CURRENT
                        </span>
                      ) : (
                        <button
                          className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded-md text-sm"
                          onClick={() => handleSetCurrent(event.id)}
                        >
                          Set Current
                        </button>
                      )}
                    </td>
                    <td className="px-6 py-4 flex gap-2 justify-center">
                      <button
                        className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded-md text-sm"
                        onClick={() => handleEdit(event)}
                      >
                        Edit
                      </button>
                      <button
                        className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded-md text-sm"
                        onClick={() => handleArchive(event.id)}
                      >
                        Archive
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="5"
                    className="px-6 py-8 text-center text-gray-500 bg-white"
                  >
                    No events available.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default ManageEvents;
