// src/admin/ArchivedEvents.js
import React, { useEffect, useState } from "react";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  addDoc,
  deleteDoc,
  doc,
  getDoc,
  setDoc,
  getDocs,
  query,
  where,
} from "firebase/firestore";
import Sidebar from "../components/Sidebar";

function ArchivedEvents() {
  const [archivedEvents, setArchivedEvents] = useState([]);
  const [showRestoreModal, setShowRestoreModal] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);

  const [showFeedbacksModal, setShowFeedbacksModal] = useState(false);
  const [archivedFeedbacks, setArchivedFeedbacks] = useState([]);
  const [feedbackLoading, setFeedbackLoading] = useState(false);

  const [detailOpen, setDetailOpen] = useState(false);
  const [selectedFeedback, setSelectedFeedback] = useState(null);

  // Load archived events list realtime
  useEffect(() => {
    const unsub = onSnapshot(collection(db, "archived_events"), (snap) => {
      const list = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
      setArchivedEvents(list);
    });

    return () => unsub();
  }, []);

  const openRestoreModal = (event) => {
    setSelectedEvent(event);
    setShowRestoreModal(true);
  };

  // Full restore: event doc, event_questions, archived feedbacks -> feedbacks
  const handleRestore = async () => {
    if (!selectedEvent) return;
    try {
      const eventId = selectedEvent.originalId || selectedEvent.id;

      // 1) Restore event doc (preserve all fields from archived copy)
      // Remove archive-specific fields if present, set restoredAt
      const eventDoc = {
        ...(selectedEvent || {}),
        archivedAt: null,
        originalId: null,
        restoredAt: new Date(),
      };
      // It's safer to avoid writing internal archival fields back; keep fields commonly used:
      await setDoc(doc(db, "events", eventId), eventDoc);

      // 2) Restore event_questions if saved on archived doc
      if (selectedEvent.event_questions) {
        await setDoc(doc(db, "event_questions", eventId), {
          questions: selectedEvent.event_questions.questions || [],
          openEnded: selectedEvent.event_questions.openEnded || [],
          createdAt: selectedEvent.event_questions.createdAt || new Date(),
          updatedAt: new Date(),
        });
      }

      // 3) Restore any archived feedbacks stored in "archived_event_feedbacks"
      // (some projects keep a separate collection for archived feedbacks)
      const feedQ1 = query(
        collection(db, "archived_event_feedbacks"),
        where("eventId", "==", eventId)
      );
      const feedSnap1 = await getDocs(feedQ1);
      for (const fbDoc of feedSnap1.docs) {
        const fb = fbDoc.data();
        await addDoc(collection(db, "feedbacks"), { ...fb, restoredAt: new Date() });
        await deleteDoc(doc(db, "archived_event_feedbacks", fbDoc.id));
      }

      // 4) Also restore archived feedbacks from "archived_feedbacks" if present
      const feedQ2 = query(collection(db, "archived_feedbacks"), where("eventId", "==", eventId));
      const feedSnap2 = await getDocs(feedQ2);
      for (const fbDoc of feedSnap2.docs) {
        const fb = fbDoc.data();
        await addDoc(collection(db, "feedbacks"), { ...fb, restoredAt: new Date() });
        await deleteDoc(doc(db, "archived_feedbacks", fbDoc.id));
      }

      // 5) Remove archived_events entry
      await deleteDoc(doc(db, "archived_events", selectedEvent.id));

      // close modal + clear selection
      setShowRestoreModal(false);
      setSelectedEvent(null);

      alert("Event restored successfully with feedbacks & questions (if present).");
    } catch (err) {
      console.error("Restore failed:", err);
      alert("Failed to restore event. See console for details.");
    }
  };

  const handleDeleteArchive = async (id) => {
    if (!confirm("Delete this archived event permanently?")) return;
    try {
      await deleteDoc(doc(db, "archived_events", id));
    } catch (err) {
      console.error("Delete archived event failed:", err);
      alert("Failed to delete archived event.");
    }
  };

  /* ----------------- View archived feedbacks modal ----------------- */
  const openFeedbacks = async (archEvent) => {
    if (!archEvent) return;
    setArchivedFeedbacks([]);
    setSelectedEvent(archEvent);
    setFeedbackLoading(true);

    try {
      const eventId = archEvent.originalId || archEvent.id;

      // aggregate from both collections you might have used
      const results = [];

      const q1 = query(collection(db, "archived_event_feedbacks"), where("eventId", "==", eventId));
      const snap1 = await getDocs(q1);
      snap1.forEach((d) => results.push({ id: d.id, ...d.data(), source: "archived_event_feedbacks" }));

      const q2 = query(collection(db, "archived_feedbacks"), where("eventId", "==", eventId));
      const snap2 = await getDocs(q2);
      snap2.forEach((d) => results.push({ id: d.id, ...d.data(), source: "archived_feedbacks" }));

      // sort newest first if there is archivedAt/createdAt
      results.sort((a, b) => {
        const ta = a.archivedAt?.toMillis?.() ?? a.createdAt?.toMillis?.() ?? 0;
        const tb = b.archivedAt?.toMillis?.() ?? b.createdAt?.toMillis?.() ?? 0;
        return tb - ta;
      });

      setArchivedFeedbacks(results);
      setShowFeedbacksModal(true);
    } catch (err) {
      console.error("Failed to load archived feedbacks:", err);
      alert("Failed to load archived feedbacks. See console.");
    } finally {
      setFeedbackLoading(false);
    }
  };

  const openFeedbackDetail = (fb) => {
    setSelectedFeedback(fb);
    setDetailOpen(true);
  };

  /* ---------------- UI ---------------- */
  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />
      <div className="flex-1 p-10 overflow-y-auto">
        <h2 className="text-3xl font-semibold text-gray-800 mb-8">Archived Events</h2>

        <div className="bg-white shadow-xl rounded-2xl overflow-hidden border border-gray-200">
          <table className="min-w-full text-left">
            <thead className="bg-gray-800 text-gray-100">
              <tr>
                <th className="px-6 py-3">Title</th>
                <th className="px-6 py-3">Location</th>
                <th className="px-6 py-3">Date</th>
                <th className="px-6 py-3 text-center">Actions</th>
              </tr>
            </thead>

            <tbody>
              {archivedEvents.length > 0 ? (
                archivedEvents.map((ev) => (
                  <tr key={ev.id} className="border-b border-gray-200 hover:bg-gray-50">
                    <td className="px-6 py-4 text-gray-800">{ev.title}</td>
                    <td className="px-6 py-4 text-gray-700">{ev.location}</td>
                    <td className="px-6 py-4 text-gray-700">{ev.date}</td>

                    <td className="px-6 py-4">
                      <div className="flex justify-center gap-2">
                        <button
                          onClick={() => openRestoreModal(ev)}
                          className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded-md text-sm"
                        >
                          Restore
                        </button>

                        <button
                          onClick={() => openFeedbacks(ev)}
                          className="bg-gray-700 hover:bg-gray-900 text-white px-3 py-1 rounded-md text-sm"
                        >
                          View Feedbacks
                        </button>

                        <button
                          onClick={() => handleDeleteArchive(ev.id)}
                          className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-md text-sm"
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="4" className="text-center px-6 py-8 text-gray-500 bg-white">
                    No archived events available.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Restore confirmation modal */}
      {showRestoreModal && selectedEvent && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-xl shadow-xl w-96">
            <h3 className="text-xl font-semibold text-gray-800 mb-4">Restore Event</h3>
            <p className="text-gray-600 mb-6">
              Restore <span className="font-medium text-gray-900">“{selectedEvent.title}”</span> back to active events?
            </p>

            <div className="flex justify-center gap-4">
              <button onClick={handleRestore} className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-md">
                Restore
              </button>
              <button onClick={() => setShowRestoreModal(false)} className="bg-gray-300 hover:bg-gray-400 px-4 py-2 rounded-md">
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Archived Feedbacks modal */}
      {showFeedbacksModal && (
        <div className="fixed inset-0 z-50 flex items-start justify-center pt-16 bg-black bg-opacity-40 p-4">
          <div className="bg-white rounded-xl shadow-xl w-full max-w-4xl max-h-[80vh] overflow-auto">
            <div className="flex items-center justify-between p-4 border-b">
              <h3 className="text-lg font-semibold">Archived Feedbacks — {selectedEvent?.title}</h3>
              <div>
                <button
                  onClick={() => {
                    setShowFeedbacksModal(false);
                    setArchivedFeedbacks([]);
                    setSelectedEvent(null);
                  }}
                  className="text-gray-600 hover:text-gray-800"
                >
                  Close ✕
                </button>
              </div>
            </div>

            <div className="p-4">
              {feedbackLoading ? (
                <div className="text-center py-8">Loading feedbacks…</div>
              ) : archivedFeedbacks.length === 0 ? (
                <div className="text-center py-8 text-gray-500">No archived feedbacks for this event.</div>
              ) : (
                <div className="overflow-x-auto">
                  <table className="min-w-full text-left">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-4 py-2 text-sm font-medium">Student ID</th>
                        <th className="px-4 py-2 text-sm font-medium">Name</th>
                        <th className="px-4 py-2 text-sm font-medium">Comment</th>
                        <th className="px-4 py-2 text-sm font-medium text-center">Sentiment</th>
                        <th className="px-4 py-2 text-sm font-medium text-center">Actions</th>
                      </tr>
                    </thead>

                    <tbody>
                      {archivedFeedbacks.map((fb) => (
                        <tr key={fb.id} className="border-t hover:bg-gray-50">
                          <td className="px-4 py-3 text-sm text-gray-700">{fb.studentID ?? fb.userId ?? "—"}</td>
                          <td className="px-4 py-3 text-sm text-gray-700">{fb.anonymous ? "Anonymous" : fb.userName ?? fb.displayName ?? fb.userEmail ?? "User"}</td>
                          <td className="px-4 py-3 text-sm text-gray-700 truncate max-w-[40ch]">{fb.comment ?? "—"}</td>
                          <td className="px-4 py-3 text-sm text-center">
                            <span className={`inline-flex items-center gap-2 px-3 py-1 rounded-full text-xs font-medium ${
                              (fb.sentiment || fb._sentiment) === "positive" ? "bg-green-100 text-green-700" :
                              (fb.sentiment || fb._sentiment) === "negative" ? "bg-red-100 text-red-700" :
                              (fb.sentiment || fb._sentiment) === "spam" ? "bg-yellow-100 text-yellow-700" :
                              "bg-gray-100 text-gray-700"
                            }`}>
                              {fb.sentiment ?? fb._sentiment ?? "neutral"}
                            </span>
                          </td>
                          <td className="px-4 py-3 text-center">
                            <div className="inline-flex gap-2">
                              <button
                                onClick={() => openFeedbackDetail(fb)}
                                className="px-3 py-1 bg-gray-700 hover:bg-gray-800 text-white rounded-md text-sm"
                              >
                                View
                              </button>
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Feedback Detail modal */}
      {detailOpen && selectedFeedback && (
        <div className="fixed inset-0 z-60 flex items-center justify-center bg-black bg-opacity-50 p-4">
          <div className="bg-white rounded-xl shadow-xl max-w-3xl w-full max-h-[85vh] overflow-auto">
            <div className="flex items-center justify-between p-5 border-b">
              <h3 className="text-lg font-semibold">Feedback Detail</h3>
              <button onClick={() => { setDetailOpen(false); setSelectedFeedback(null); }} className="text-gray-600 hover:text-gray-800">Close ✕</button>
            </div>

            <div className="p-6 space-y-4">
              <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
                <div>
                  <div className="text-sm text-gray-500">Submitted</div>
                  <div className="text-sm text-gray-700">{String(selectedFeedback.createdAt ?? selectedFeedback.archivedAt ?? "—")}</div>
                </div>

                <div>
                  <div className="text-sm text-gray-500">Average Rating</div>
                  <div className="text-2xl font-bold text-gray-800">{(Array.isArray(selectedFeedback.ratings) ? selectedFeedback.ratings.reduce((a,b)=>a+b,0)/ (selectedFeedback.ratings.length||1) : selectedFeedback.rating ?? "—") } <span className="text-yellow-500">★</span></div>
                </div>

                <div>
                  <div className="text-sm text-gray-500">Sentiment</div>
                  <div className={`px-3 py-1 rounded-full text-xs font-semibold ${selectedFeedback.sentiment === "positive" ? "bg-green-100 text-green-700" : selectedFeedback.sentiment === "negative" ? "bg-red-100 text-red-700" : selectedFeedback.sentiment === "spam" ? "bg-yellow-100 text-yellow-700" : "bg-gray-100 text-gray-700"}`}>
                    {selectedFeedback.sentiment ?? "neutral"}
                  </div>
                </div>
              </div>

              <div>
                <div className="text-sm text-gray-500 mb-1">Comment</div>
                <div className="bg-gray-50 border p-3 rounded-md text-gray-700">{selectedFeedback.comment ?? <em>No comment</em>}</div>
              </div>

              <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                <div className="bg-gray-50 p-4 rounded-md">
                  <div className="text-sm font-medium text-gray-600 mb-2">Question Ratings</div>
                  <ul className="space-y-2 text-sm">
                    {(selectedFeedback.ratings ? (Array.isArray(selectedFeedback.ratings) ? selectedFeedback.ratings : Object.values(selectedFeedback.ratings)) : []).map((r, i) => (
                      <li key={i} className="flex justify-between">
                        <span className="text-gray-700">Q{i+1}</span>
                        <span className="font-semibold">{Number(r)}</span>
                      </li>
                    ))}
                    {!selectedFeedback.ratings && <div className="text-gray-500 italic">No ratings</div>}
                  </ul>
                </div>

                <div className="bg-gray-50 p-4 rounded-md">
                  <div className="text-sm font-medium text-gray-600 mb-2">Open-ended Answers</div>
                  {(selectedFeedback.answers ? (Array.isArray(selectedFeedback.answers) ? selectedFeedback.answers : Object.values(selectedFeedback.answers)) : []).length ? (
                    <ol className="list-decimal pl-5 space-y-2 text-sm">
                      {(selectedFeedback.answers ? (Array.isArray(selectedFeedback.answers) ? selectedFeedback.answers : Object.values(selectedFeedback.answers)) : []).map((a, idx) => (
                        <li key={idx} className="bg-white border p-2 rounded-md">{a}</li>
                      ))}
                    </ol>
                  ) : (
                    <div className="text-gray-500 italic">No open-ended answers.</div>
                  )}
                </div>
              </div>

              <div className="flex justify-between items-center">
                <div>
                  <div className="text-sm text-gray-500">Student ID</div>
                  <div className="text-sm text-gray-700">{selectedFeedback.studentID ?? selectedFeedback.userId ?? "—"}</div>
                </div>

                <div>
                  <div className="text-sm text-gray-500">Anonymous</div>
                  <div className="text-sm text-gray-700">{selectedFeedback.anonymous ? "Yes" : "No"}</div>
                </div>
              </div>

              <div className="flex justify-end">
                <button onClick={() => { setDetailOpen(false); setSelectedFeedback(null); }} className="px-4 py-2 bg-white border rounded-md hover:bg-gray-100">Close</button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ArchivedEvents;
