import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import { collection, query, where, onSnapshot, getDoc, doc, orderBy, getDoc as getEventDoc } from "firebase/firestore";

export default function Invalidated() {
  const [invalidatedUsers, setInvalidatedUsers] = useState([]);
  const [search, setSearch] = useState("");
  const [filtered, setFiltered] = useState([]);
  const [currentEventName, setCurrentEventName] = useState("Loading...");

  // 笨 Fetch current event name (assuming only 1 active event)
  useEffect(() => {
    const fetchEventName = async () => {
      try {
        const eventRef = doc(db, "currentEvent", "active"); // 沐ｹ Change if your structure is different
        const eventSnap = await getEventDoc(eventRef);
        if (eventSnap.exists()) {
          setCurrentEventName(eventSnap.data().name || "Unnamed Event");
        } else {
          setCurrentEventName("No Active Event");
        }
      } catch (error) {
        console.error("Error fetching event name:", error);
        setCurrentEventName("Unknown Event");
      }
    };
    fetchEventName();
  }, []);

  // 笨 Real-time fetch of invalidated users
  useEffect(() => {
    const qRef = query(
      collection(db, "liveAttendance"),
      where("status", "==", "invalidated"),
      orderBy("timestamp", "desc")
