    const unsub = onSnapshot(qRef, (snap) => {
      if (snap.empty) setCurrentEvent(null);
      else {
        const d = snap.docs[0];
        setCurrentEvent({ id: d.id, ...d.data() });
      }
    });
    return () => unsub();
  }, []);

  // 笨 Format Firestore timestamps
  const fmtDate = (ts) => {
    try {
      if (!ts) return "窶能

en-US", {
        year: "numeric",
        month: "short",
        day: "numeric",
        hour: "numeric",
        minute: "2-digit",
        hour12: true,
      });
    } catch {
      return "窶能

Validated") {
      results = results.filter((l) => l.status === "validated");
    } else if (statusFilter === "Invalidated") {
      results = results.filter((l) => l.status !== "validated");
    }

    setFiltered(results);
  }, [search, logs, currentEvent, statusFilter]);

  // 笨 Count Summary
  const counts = useMemo(() => {
    const total = filtered.length;
    const valid = filtered.filter((l) => l.status === "validated").length;
    const invalid = filtered.filter((l) => l.status !== "validated").length;
    return { total, valid, invalid };
  }, [filtered]);

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">
        {/* 笨 Header */}
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-3xl font-semibold text-gray-800">Attendance Logs</h1>
          <div className="text-right text-sm text-gray-600">
            <div>
              Total:{" "}
              <span className="font-semibold text-yellow-500">{counts.total}</span>
            </div>
            <div className="text-yellow-600">Validated: {counts.valid}</div>
            <div className="text-yellow-600">Invalidated: {counts.invalid}</div>
          </div>
        </div>

        {/* 笨 Current Event Box */}
        <div className="bg-white rounded-xl shadow-md p-6 mb-6">
          <h2 className="text-lg font-semibold text-gray-800 mb-2">
            Current Event
          </h2>
          {currentEvent ? (
            <div>
              <p className="text-xl font-bold text-yellow-500">
                {currentEvent.title || "Untitled Event"}
              </p>
              <p className="text-sm text-gray-600 mt-1">
                {currentEvent.location ? `@ ${currentEvent.location}` : "Location: TBA"}
              </p>
              <p className="text-xs text-gray-400 mt-1">
                {currentEvent.date || "Date: TBA"}
              </p>
            </div>
          ) : (
            <p className="text-gray-500 italic">No current event selected.</p>
          )}
        </div>

        {/* 笨 Search and Filters */}
        <div className="flex flex-col md:flex-row md:items-center md:justify-between mb-6 gap-4">
          <input
            type="text"
            placeholder="Search by Student ID or Email..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="flex-1 px-4 py-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-yellow-400 focus:outline-none"
          />
        </div>

        {/* 笨 Filter Buttons */}
        <div className="flex space-x-3 mb-6">
          {["All", "Validated", "Invalidated"].map((type) => (
            <button
              key={type}
              onClick={() => setStatusFilter(type)}
              className={`px-5 py-2 rounded-lg font-medium border transition ${
                statusFilter === type
                  ? "bg-yellow-400 text-white border-yellow-400"
                  : "bg-white text-gray-700 border-gray-300 hover:bg-gray-100"
              }`}
            >
              {type}
            </button>
          ))}
        </div>

        {/* 笨 Attendance Logs Table */}
        <div className="bg-white rounded-xl shadow overflow-hidden">
          <table className="min-w-full table-auto border-collapse">
            <thead className="bg-gray-50 text-gray-700 text-sm font-semibold">
              <tr>
                <th className="px-6 py-3 text-left">Student ID</th>
                <th className="px-6 py-3 text-left">Email</th>
                <th className="px-6 py-3 text-left">Date</th>
                <th className="px-6 py-3 text-left">Status</th>
              </tr>
            </thead>
            <tbody>
              {filtered.length > 0 ? (
                filtered.map((log) => (
                  <tr key={log.id} className="border-t hover:bg-gray-50 transition">
                    <td className="px-6 py-3 text-sm text-gray-700">
                      {log.studentID || "窶能

px-6 py-3 text-xs text-gray-500">
                      {fmtDate(log.timestamp)}
                    </td>
                    <td className="px-6 py-3">
                      <span
                        className={`px-3 py-1 text-xs font-semibold rounded-full ${
                          log.status === "validated"
                            ? "bg-yellow-100 text-yellow-700"
                            : "bg-yellow-50 text-yellow-700"
                        }`}
                      >
                        {log.status === "validated" ? "Validated" : "Invalidated"}
                      </span>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="4"
                    className="px-6 py-10 text-center text-gray-500 italic"
                  >
                    No attendance logs found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        <p className="text-xs text-gray-400 mt-4">
          * This table updates in real-time as users validate attendance from the
          mobile app.
        </p>
      </div>
    </div>
  );
}
