      const list = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
      setEvents(list);
    });
    return () => unsub();
  }, []);

  // 笨 Format Firestore timestamps
  const fmtDate = (ts) => {
    try {
      if (!ts) return "窶能

