import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  FlatList,
  StyleSheet,
  ActivityIndicator,
  TouchableOpacity,
} from "react-native";
import { collection, addDoc, getDocs, query, orderBy, Timestamp } from "firebase/firestore";
import { auth, db } from "../../src/firebase";

export async function logAttendance(status) {
  try {
    const user = auth.currentUser;
    if (!user) throw new Error("User not logged in");

    // ðŸ” Fetch user's ID number from Firestore
    const usersSnap = await getDocs(collection(db, "users"));
    let studentData = null;
    usersSnap.forEach((doc) => {
      const data = doc.data();
      if (data.email.toLowerCase() === user.email.toLowerCase()) {
        studentData = data;
      }
    });

    if (!studentData) throw new Error("Student record not found");

    const logRef = collection(db, "attendanceLogs");
    await addDoc(logRef, {
      studentId: studentData.idNumber,
      email: user.email,
      date: Timestamp.now(),
      status,
    });

    console.log("âœ… Attendance logged successfully");
  } catch (error) {
    console.error("âŒ Error logging attendance:", error);
  }
}

export default function AttendanceLogs() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchLogs = async () => {
    try {
      const q = query(collection(db, "attendanceLogs"), orderBy("date", "desc"));
      const snapshot = await getDocs(q);

      const data = snapshot.docs.map((doc) => {
        const d = doc.data();
        return {
          id: doc.id,
          studentId: d.studentId || "-",
          email: d.email || "-",
          date: d.date?.toDate().toLocaleString() || "-",
          status: d.status || "-",
        };
      });

      setLogs(data);
    } catch (error) {
      console.error("Error fetching logs:", error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchLogs();
  }, []);

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Attendance Logs</Text>

      {loading ? (
        <ActivityIndicator size="large" color="#FFD700" style={{ marginTop: 40 }} />
      ) : (
        <FlatList
          data={logs}
          keyExtractor={(item) => item.id}
          renderItem={({ item }) => (
            <View style={styles.row}>
              <Text style={[styles.cell, { flex: 1.2 }]}>{item.studentId}</Text>
              <Text style={[styles.cell, { flex: 2 }]}>{item.email}</Text>
              <Text style={[styles.cell, { flex: 2 }]}>{item.date}</Text>
              <Text
                style={[
                  styles.cell,
                  { flex: 1, color: item.status === "Present" ? "#22c55e" : "#ef4444" },
                ]}
              >
                {item.status}
              </Text>
            </View>
          )}
          ListHeaderComponent={() => (
            <View style={[styles.row, styles.headerRow]}>
              <Text style={[styles.headerCell, { flex: 1.2 }]}>Student ID</Text>
              <Text style={[styles.headerCell, { flex: 2 }]}>Email</Text>
              <Text style={[styles.headerCell, { flex: 2 }]}>Date</Text>
              <Text style={[styles.headerCell, { flex: 1 }]}>Status</Text>
            </View>
          )}
        />
      )}

      <TouchableOpacity onPress={fetchLogs} style={styles.refreshBtn}>
        <Text style={styles.refreshText}>â†» Refresh Logs</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#0d1736",
    padding: 16,
    paddingTop: 40,
  },
  header: {
    color: "#fff",
    fontSize: 28,
    fontWeight: "900",
    marginBottom: 12,
    textAlign: "center",
  },
  row: {
    flexDirection: "row",
    borderBottomWidth: 1,
    borderColor: "#1c2239",
    paddingVertical: 10,
    alignItems: "center",
  },
  headerRow: {
    backgroundColor: "#1c2239",
    borderRadius: 6,
    marginBottom: 6,
    paddingVertical: 10,
  },
  headerCell: {
    color: "#facc15",
    fontWeight: "700",
    textAlign: "left",
    fontSize: 14,
  },
  cell: {
    color: "#fff",
    textAlign: "left",
    fontSize: 13,
  },
  refreshBtn: {
    marginTop: 18,
    backgroundColor: "#facc15",
    paddingVertical: 12,
    borderRadius: 8,
    alignItems: "center",
  },
  refreshText: {
    color: "#111827",
    fontWeight: "800",
    fontSize: 15,
  },
});
