import React, { useEffect, useState } from "react";
import { View, Text, ScrollView, ActivityIndicator } from "react-native";
import { collection, doc, onSnapshot, orderBy, query, setDoc, serverTimestamp } from "firebase/firestore";
import { db, auth } from "../src/firebase";
import { SafeAreaView, useSafeAreaInsets } from "react-native-safe-area-context";
import Ionicons from "@expo/vector-icons/Ionicons";

const C = {
  bg: "#1b1f38",
  accent: "#FFD700",
  muted: "#b0b6d3",
  border: "rgba(255,255,255,0.15)",
  success: "#4ade80",
  danger: "#ef4444",
};

// ðŸ”§ Helper to format Firestore timestamps nicely
const formatDateTime = (timestamp) => {
  if (!timestamp) return "";
  const date =
    timestamp?.seconds != null
      ? new Date(timestamp.seconds * 1000)
      : new Date(timestamp);
  return date.toLocaleString("en-US", {
    year: "numeric",
    month: "long",
    day: "numeric",
    hour: "numeric",
    minute: "2-digit",
    hour12: true,
  });
};

// ðŸ§± Automatically create the Firestore structure for logs
export async function logAttendance({
  userId,
  eventId,
  eventTitle,
  status,
  distance,
}) {
  try {
    const id = `${userId}_${eventId}_${Date.now()}`;
    await setDoc(doc(collection(db, "attendanceLogs"), id), {
      userId,
      eventId,
      eventTitle,
      status,
      distance,
      timestamp: serverTimestamp(),
    });
    console.log("âœ… Attendance log added:", id);
  } catch (err) {
    console.error("âŒ Error creating attendance log:", err);
  }
}

export default function AttendanceLogs() {
  const insets = useSafeAreaInsets();
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const uid = auth.currentUser?.uid;
    if (!uid) return;

    // Listen for this user's logs (auto-created by code)
    const qRef = query(
      collection(db, "attendanceLogs"),
      orderBy("timestamp", "desc")
    );

    const unsub = onSnapshot(
      qRef,
      (snap) => {
        const data = snap.docs
          .map((d) => ({ id: d.id, ...d.data() }))
          .filter((d) => d.userId === uid);
        setLogs(data);
        setLoading(false);
      },
      (error) => {
        console.error("âŒ Firestore listener error:", error);
        setLoading(false);
      }
    );

    return () => unsub();
  }, []);

  return (
    <SafeAreaView
      style={{
        flex: 1,
        backgroundColor: C.bg,
        paddingTop: Math.max(insets.top, 20),
      }}
    >
      <ScrollView
        contentContainerStyle={{
          padding: 20,
          paddingBottom: 80,
        }}
        showsVerticalScrollIndicator={false}
      >
        <Text
          style={{
            color: "#fff",
            fontSize: 22,
            fontFamily: "Poppins_700Bold",
            textAlign: "center",
            marginBottom: 20,
          }}
        >
          Attendance Logs
        </Text>

        {loading ? (
          <ActivityIndicator size="large" color={C.accent} />
        ) : logs.length === 0 ? (
          <Text
            style={{
              color: C.muted,
              fontFamily: "Poppins_400Regular",
              textAlign: "center",
              marginTop: 40,
            }}
          >
            No attendance records yet.
          </Text>
        ) : (
          logs.map((log) => (
            <View
              key={log.id}
              style={{
                backgroundColor: "rgba(255,255,255,0.05)",
                borderRadius: 16,
                padding: 16,
                marginBottom: 14,
                borderWidth: 0.8,
                borderColor: C.border,
              }}
            >
              <View
                style={{
                  flexDirection: "row",
                  alignItems: "center",
                  justifyContent: "space-between",
                  marginBottom: 8,
                }}
              >
                <Text
                  style={{
                    color: "#fff",
                    fontFamily: "Poppins_600SemiBold",
                    fontSize: 16,
                    flex: 1,
                  }}
                  numberOfLines={1}
                >
                  {log.eventTitle || "Unknown Event"}
                </Text>

                <Ionicons
                  name={
                    log.status === "validated"
                      ? "checkmark-circle"
                      : "close-circle"
                  }
                  size={22}
                  color={log.status === "validated" ? C.success : C.danger}
                />
              </View>

              <Text
                style={{
                  color: C.muted,
                  fontFamily: "Poppins_400Regular",
                  marginBottom: 4,
                }}
              >
                Status:{" "}
                <Text
                  style={{
                    color:
                      log.status === "validated" ? C.success : C.danger,
                    fontFamily: "Poppins_600SemiBold",
                  }}
                >
                  {log.status.toUpperCase()}
                </Text>
              </Text>

              <Text
                style={{
                  color: C.muted,
                  fontFamily: "Poppins_400Regular",
                  marginBottom: 4,
                }}
              >
                Distance: {log.distance?.toFixed(1) || 0} meters
              </Text>

              <Text
                style={{
                  color: C.muted,
                  fontFamily: "Poppins_400Regular",
                }}
              >
                {formatDateTime(log.timestamp)}
              </Text>
            </View>
          ))
        )}
      </ScrollView>
    </SafeAreaView>
  );
}
