// src/admin/ArchivedFeedbacks.js
import React, { useEffect, useMemo, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  query,
  where,
  orderBy,
  addDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";
import Swal from "sweetalert2";

const fmtDate = (ts) => {
  try {
    const d = ts?.toDate?.() instanceof Date ? ts.toDate() : new Date(ts || Date.now());
    return d.toLocaleString();
  } catch {
    return "—";
  }
};

export default function ArchivedFeedbacks() {
  const { eventId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();

  const [eventName] = useState(location?.state?.eventName || "Event");
  const [feedbacks, setFeedbacks] = useState([]);

  useEffect(() => {
    if (!eventId) return;
    const qRef = query(
      collection(db, "archived_feedbacks"),
      where("eventId", "==", eventId),
      orderBy("archivedAt", "desc")
    );
    const unsub = onSnapshot(qRef, (snap) => {
      const list = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
      setFeedbacks(list);
    });
    return () => unsub();
  }, [eventId]);

  const handleRestore = async (id, fb) => {
    const confirm = await Swal.fire({
      title: "Restore this feedback?",
      text: "It will move back to Feedbacks.",
      icon: "question",
      showCancelButton: true,
      confirmButtonColor: "#16a34a", // green
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Yes, restore",
    });
    if (!confirm.isConfirmed) return;
    try {
      await addDoc(collection(db, "feedbacks"), {
        ...fb,
        restoredAt: new Date(),
      });
      await deleteDoc(doc(db, "archived_feedbacks", id));
      Swal.fire({ icon: "success", title: "Restored", timer: 1400, showConfirmButton: false });
    } catch (e) {
      console.error(e);
      Swal.fire({ icon: "error", title: "Failed to restore", text: e?.message || "" });
    }
  };

  return (
    <div className="flex h-screen bg-gray-50 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">
        <div className="flex items-start justify-between mb-6">
          <h1 className="text-3xl font-semibold text-gray-800">
            Archived Feedbacks — <span className="text-yellow-500">{eventName}</span>
          </h1>
          <button
            onClick={() =>
              navigate(`/admin/event-feedbacks/${eventId}`, { state: { eventName } })
            }
            className="px-4 py-2 rounded-md text-sm border bg-white hover:bg-gray-100 text-gray-700 shadow-sm"
          >
            Back to Feedbacks
          </button>
        </div>

        {/* Feedbacks Table */}
        <div className="bg-[#1E293B] shadow-lg rounded-xl overflow-hidden">
          <table className="min-w-full text-left text-gray-800">
            <thead className="bg-[#0F172A] text-white">
              <tr>
                <th className="px-6 py-3 text-sm font-medium">User</th>
                <th className="px-6 py-3 text-sm font-medium">Rating</th>
                <th className="px-6 py-3 text-sm font-medium">Comment</th>
                <th className="px-6 py-3 text-sm font-medium">Date</th>
                <th className="px-6 py-3 text-sm font-medium text-center">Actions</th>
              </tr>
            </thead>
            <tbody>
              {feedbacks.length > 0 ? (
                feedbacks.map((fb) => (
                  <tr
                    key={fb.id}
                    className="border-b border-gray-300 hover:bg-gray-100 transition bg-white"
                  >
                    <td className="px-6 py-4">
                      {fb?.anonymous ? <em>Anonymous</em> : fb?.userName || fb?.userEmail || "User"}
                    </td>
                    <td className="px-6 py-4 text-yellow-600 font-semibold">
                      {fb?.rating ?? "—"} ★
                    </td>
                    <td className="px-6 py-4">
                      {fb?.comment || <span className="italic text-gray-400">No comment</span>}
                    </td>
                    <td className="px-6 py-4">{fmtDate(fb?.createdAt)}</td>
                    <td className="px-6 py-4 flex gap-2 justify-center">
                      <button
                        onClick={() => handleRestore(fb.id, fb)}
                        className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded-md text-sm"
                      >
                        Restore
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="5"
                    className="px-6 py-8 text-center text-gray-500 bg-white"
                  >
                    No archived feedbacks available.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
