// src/pages/ArchivedFeedbacks.js
import React, { useEffect, useMemo, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  query,
  where,
  orderBy,
  addDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";
import Swal from "sweetalert2";

/** Show sample cards if Firestore is empty (optional) */
const USE_SAMPLE_WHEN_EMPTY = true;

const deriveSentimentHeuristic = (fb) => {
  const rating = Number(fb?.rating);
  const text = (fb?.comment || "").toLowerCase();
  const spamWords = ["http://", "https://", "buy now", "promo", "free $$$", "visit my"];
  const isSpam = spamWords.some((w) => text.includes(w)) || fb?.isSpam === true;
  if (isSpam) return "spam";
  if (!Number.isNaN(rating)) {
    if (rating >= 4) return "positive";
    if (rating <= 2) return "negative";
  }
  return "neutral";
};

const fmtDate = (ts) => {
  try {
    const d = ts?.toDate?.() instanceof Date ? ts.toDate() : new Date(ts || Date.now());
    return d.toLocaleString();
  } catch {
    return "—";
  }
};

const Pill = ({ active, onClick, children }) => (
  <button
    onClick={onClick}
    className={
      "px-4 py-2 rounded-full text-sm transition " +
      (active ? "bg-blue-600 text-white shadow" : "bg-white text-gray-700 hover:bg-gray-100 border")
    }
  >
    {children}
  </button>
);

const ArchivedCard = ({ fb, onRestore, onDelete }) => {
  const sentiment = (fb?.sentiment || fb?._sentiment || "neutral").toLowerCase();
  const chipColor =
    sentiment === "positive"
      ? "bg-green-100 text-green-700"
      : sentiment === "negative"
      ? "bg-red-100 text-red-700"
      : sentiment === "spam"
      ? "bg-yellow-100 text-yellow-700"
      : "bg-gray-100 text-gray-700";

  return (
    <div className="bg-white rounded-xl shadow p-5 flex flex-col gap-3">
      <div className="flex items-start justify-between gap-3">
        <div>
          <div className="text-sm text-gray-500">
            {fb?.anonymous ? <em>Anonymous</em> : fb?.userName || fb?.userEmail || "User"}
          </div>
          <div className="text-xs text-gray-400">
            Archived: {fmtDate(fb?.archivedAt)}{" "}
            {fb?.createdAt ? <>• Submitted: {fmtDate(fb?.createdAt)}</> : null}
          </div>
        </div>
        <div className={`px-3 py-1 rounded-full text-xs font-medium ${chipColor} capitalize`}>
          {sentiment}
        </div>
      </div>

      <div className="flex items-center gap-2">
        <div className="text-lg font-semibold text-gray-800">{fb?.rating ?? "—"}</div>
        <div className="text-yellow-500">★</div>
      </div>

      <div className="text-gray-700">
        {fb?.comment ? fb.comment : <span className="italic text-gray-400">No comment</span>}
      </div>

      {Array.isArray(fb?.answers) && fb.answers.length > 0 && (
        <div className="mt-1 border-t pt-3">
          <div className="text-sm font-medium text-gray-600 mb-2">Question Responses</div>
          <ul className="space-y-1">
            {fb.answers.map((a, idx) => (
              <li key={idx} className="text-sm text-gray-600">
                <span className="font-medium">{a?.question}</span>
                {": "}
                <span>{a?.answer}</span>
              </li>
            ))}
          </ul>
        </div>
      )}

      <div className="mt-2 flex gap-2">
        <button
          onClick={onRestore}
          className="px-3 py-2 text-sm rounded-md bg-emerald-600 hover:bg-emerald-700 text-white transition"
        >
          Restore
        </button>
        <button
          onClick={onDelete}
          className="px-3 py-2 text-sm rounded-md bg-red-600 hover:bg-red-700 text-white transition"
        >
          Delete
        </button>
      </div>
    </div>
  );
};

export default function ArchivedFeedbacks() {
  const { eventId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();

  const [eventName] = useState(location?.state?.eventName || "Event");
  const [items, setItems] = useState([]);
  const [active, setActive] = useState(
    new URLSearchParams(location.search).get("sentiment") || "all"
  );

  // Live archived feedbacks for this event
  useEffect(() => {
    if (!eventId) return;
    const qRef = query(
      collection(db, "archived_feedbacks"),
      where("eventId", "==", eventId),
      orderBy("archivedAt", "desc")
    );
    const unsub = onSnapshot(qRef, (snap) => {
      const list = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
      setItems(list);
    });
    return () => unsub();
  }, [eventId]);

  // optional sample when empty
  const withSample = useMemo(() => {
    if (items.length > 0 || !USE_SAMPLE_WHEN_EMPTY) return items;
    return [
      {
        id: "s1",
        userName: "Archived Jane",
        rating: 5,
        comment: "Keeping this for records.",
        sentiment: "positive",
        archivedAt: new Date(),
        createdAt: new Date(),
      },
    ];
  }, [items]);

  const enriched = useMemo(
    () =>
      withSample.map((fb) => ({
        ...fb,
        _sentiment: fb?.sentiment || deriveSentimentHeuristic(fb),
      })),
    [withSample]
  );

  const stats = useMemo(() => {
    const ratings = enriched.map((f) => Number(f.rating)).filter((n) => !Number.isNaN(n));
    const avg = ratings.length
      ? Number((ratings.reduce((a, b) => a + b, 0) / ratings.length).toFixed(2))
      : 0;
    return { avg };
  }, [enriched]);

  const visible = useMemo(() => {
    if (active === "all") return enriched;
    return enriched.filter((f) => f._sentiment === active);
  }, [active, enriched]);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    if (active === "all") params.delete("sentiment");
    else params.set("sentiment", active);
    navigate({ search: params.toString() }, { replace: true });
  }, [active]); // eslint-disable-line

  // Actions: restore (move to 'feedbacks') & delete (permanent)
  const handleRestore = async (id, fb) => {
    const confirm = await Swal.fire({
      title: "Restore this feedback?",
      text: "It will be moved back to active Feedbacks.",
      icon: "question",
      showCancelButton: true,
      confirmButtonColor: "#10b981",
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Yes, restore",
    });
    if (!confirm.isConfirmed) return;
    try {
      await addDoc(collection(db, "feedbacks"), {
        ...fb,
        restoredAt: new Date(),
      });
      await deleteDoc(doc(db, "archived_feedbacks", id));
      Swal.fire({ icon: "success", title: "Restored", timer: 1300, showConfirmButton: false });
    } catch (e) {
      console.error(e);
      Swal.fire({ icon: "error", title: "Failed to restore", text: e?.message || "" });
    }
  };

  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "Delete this archived feedback?",
      text: "This cannot be undone.",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#e74c3c",
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Yes, delete",
    });
    if (!confirm.isConfirmed) return;
    try {
      await deleteDoc(doc(db, "archived_feedbacks", id));
      Swal.fire({ icon: "success", title: "Deleted", timer: 1200, showConfirmButton: false });
    } catch (e) {
      console.error(e);
      Swal.fire({ icon: "error", title: "Failed to delete", text: e?.message || "" });
    }
  };

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">
        {/* header with back link on right */}
        <div className="flex items-start justify-between mb-1">
          <h1 className="text-3xl font-semibold text-gray-800">
            Archived Feedbacks — <span className="text-blue-600">{eventName}</span>
          </h1>

          <button
            onClick={() =>
              navigate(`/admin/event-feedbacks/${eventId}`, {
                state: { eventName },
              })
            }
            className="px-4 py-2 rounded-full text-sm border bg-white hover:bg-gray-100 text-gray-700 shadow-sm"
          >
            Back to Feedbacks
          </button>
        </div>

        {/* Only Average Rating */}
        <p className="text-gray-500 mb-6">
          Average Rating:{" "}
          <span className="font-semibold text-gray-700">
            {stats.avg} <span className="text-yellow-500">★</span>
          </span>
        </p>

        {/* Pills */}
        <div className="flex flex-wrap gap-3 mb-6">
          <Pill active={active === "all"} onClick={() => setActive("all")}>
            All
          </Pill>
          <Pill active={active === "positive"} onClick={() => setActive("positive")}>
            Positive
          </Pill>
          <Pill active={active === "negative"} onClick={() => setActive("negative")}>
            Negative
          </Pill>
          <Pill active={active === "spam"} onClick={() => setActive("spam")}>
            Spam
          </Pill>
        </div>

        {/* Cards grid */}
        {visible.length === 0 ? (
          <div className="bg-white rounded-xl shadow p-8 text-center text-gray-500">
            No archived feedbacks in this category.
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {visible.map((fb) => (
              <ArchivedCard
                key={fb.id}
                fb={fb}
                onRestore={() => handleRestore(fb.id, fb)}
                onDelete={() => handleDelete(fb.id)}
              />
            ))}
          </div>
        )}

        <p className="text-xs text-gray-400 mt-6">
          Tip: Once VADER sentiment is stored on each archived doc, these filters can also be
          pushed into Firestore queries.
        </p>
      </div>
    </div>
  );
}
