import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  getCountFromServer,
  query,
  where,
  onSnapshot,
  limit,
  orderBy,
  updateDoc,
  doc,
} from "firebase/firestore";
import Swal from "sweetalert2";

export default function Dashboard() {
  const navigate = useNavigate();

  const [stats, setStats] = useState({
    feedbacks: 0,
    attendanceTotal: 0,
    validated: 0,
    invalidated: 0,
  });

  const [currentEvent, setCurrentEvent] = useState(null);
  const [loadingEventStats, setLoadingEventStats] = useState(true);
  const [toggling, setToggling] = useState(false);

  /* ----------------------------------------------------
      GET CURRENT EVENT
  ---------------------------------------------------- */
  useEffect(() => {
    const qRef = query(collection(db, "events"), where("isCurrent", "==", true), limit(1));
    const unsub = onSnapshot(qRef, (snap) => {
      if (snap.empty) {
        setCurrentEvent(null);
        return;
      }
      const d = snap.docs[0];
      setCurrentEvent({ id: d.id, ...d.data() });
    });

    return () => unsub();
  }, []);

  /* ----------------------------------------------------
      LOAD EVENT-BASED STATS WHEN CURRENT EVENT CHANGES
  ---------------------------------------------------- */
  useEffect(() => {
    if (!currentEvent?.id) return;

    setLoadingEventStats(true);

    /* ------------------------------
       1. FEEDBACK COUNT PER EVENT
    ------------------------------ */
    (async () => {
      try {
        const feedbackSnap = await getCountFromServer(
          query(collection(db, "feedbacks"), where("eventId", "==", currentEvent.id))
        );

        setStats((prev) => ({
          ...prev,
          feedbacks: feedbackSnap.data().count,
        }));
      } catch (err) {
        console.error("Error loading feedback count:", err);
      }
    })();

    /* ------------------------------
       2. ATTENDANCE LOGS (PER EVENT)
    ------------------------------ */
    const logQuery = query(
      collection(db, "attendanceLogs"),
      where("eventId", "==", currentEvent.id),
      orderBy("timestamp", "desc")
    );

    const unsub1 = onSnapshot(logQuery, (snap) => {
      const total = snap.size;
      setStats((prev) => ({ ...prev, attendanceTotal: total }));
    });

    /* ------------------------------
       3. VALIDATED / INVALIDATED
    ------------------------------ */
    const unsub2 = onSnapshot(logQuery, (snap) => {
      const latestMap = new Map();

      snap.docs.forEach((docSnap) => {
        const data = docSnap.data();
        const studentID = data.studentID || data.userId || "unknown";
        if (!latestMap.has(studentID)) {
          latestMap.set(studentID, data);
        }
      });

      const latest = Array.from(latestMap.values());
      const validated = latest.filter((d) => d.status === "validated").length;
      const invalidated = latest.filter((d) => d.status !== "validated").length;

      setStats((prev) => ({
        ...prev,
        validated,
        invalidated,
      }));

      setLoadingEventStats(false);
    });

    return () => {
      unsub1();
      unsub2();
    };
  }, [currentEvent]);

  /* ----------------------------------------------------
      TOGGLE FEEDBACK FORM
  ---------------------------------------------------- */
  const handleToggleFeedback = async () => {
    if (!currentEvent?.id) return;
    const next = !Boolean(currentEvent.feedbackOpen);
    setToggling(true);
    try {
      await updateDoc(doc(db, "events", currentEvent.id), {
        feedbackOpen: next,
      });
      Swal.fire({
        icon: "success",
        title: next ? "Feedback Form Opened" : "Feedback Form Closed",
        timer: 1400,
        showConfirmButton: false,
        position: "top-end",
        toast: true,
      });
    } catch (e) {
      Swal.fire({
        icon: "error",
        title: "Failed to update",
        text: e?.message || "",
      });
    } finally {
      setToggling(false);
    }
  };

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">

        <h1 className="text-3xl font-semibold mb-10 text-gray-800">
          Welcome back, <span className="text-yellow-500">Admin!</span>
        </h1>

        {/* ----------------------------------------------------
              NO CURRENT EVENT?
        ---------------------------------------------------- */}
        {!currentEvent ? (
          <div className="text-center text-gray-600 mt-20">
            <p className="text-xl mb-3">No current event selected.</p>
            <button
              onClick={() => navigate("/admin/manage-events")}
              className="bg-gray-800 hover:bg-gray-900 text-white px-6 py-2 rounded-lg"
            >
              Go to Manage Events
            </button>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">

            {/* CURRENT EVENT CARD */}
            <div className="bg-white rounded-xl shadow-md p-6 hover:shadow-lg transition">
              <h2 className="text-lg font-semibold text-gray-800 mb-2">Current Event</h2>

              <p className="text-xl font-bold text-yellow-500">
                {currentEvent.title}
              </p>
              <p className="text-sm text-gray-500 mt-1">
                @ {currentEvent.location || "Location TBA"}
              </p>
              <p className="text-xs text-gray-400 mt-1">
                {currentEvent.date || "Date TBA"}
              </p>

              {/* FEEDBACK TOGGLE */}
              <div className="mt-5">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm font-medium text-gray-700">Feedback Form</p>
                    <p className="text-xs text-gray-500">
                      Open or close feedback submission for this event.
                    </p>
                  </div>

                  <button
                    onClick={handleToggleFeedback}
                    className={`relative inline-flex h-6 w-11 items-center rounded-full transition ${
                      currentEvent.feedbackOpen ? "bg-yellow-500" : "bg-gray-300"
                    }`}
                  >
                    <span
                      className={`inline-block h-5 w-5 transform bg-white rounded-full shadow transition ${
                        currentEvent.feedbackOpen ? "translate-x-5" : "translate-x-1"
                      }`}
                    />
                  </button>
                </div>

                <div className="mt-3">
                  <span
                    className={`inline-flex items-center gap-2 px-3 py-1 rounded-full text-xs font-semibold ${
                      currentEvent.feedbackOpen
                        ? "bg-yellow-100 text-yellow-700"
                        : "bg-gray-100 text-gray-700"
                    }`}
                  >
                    <span
                      className={`h-2 w-2 rounded-full ${
                        currentEvent.feedbackOpen ? "bg-yellow-500" : "bg-gray-400"
                      }`}
                    />
                    {currentEvent.feedbackOpen ? "OPEN" : "CLOSED"}
                  </span>
                </div>
              </div>
            </div>

            {/* FEEDBACK COUNT PER EVENT */}
            <div className="bg-white rounded-xl shadow-md p-6 hover:shadow-lg">
              <h2 className="text-lg font-semibold text-gray-800 mb-2">Feedback Count</h2>
              <p className="text-4xl font-bold text-yellow-500">{stats.feedbacks}</p>
            </div>

            {/* VALIDATED */}
            <div className="bg-white rounded-xl shadow-md p-6 hover:shadow-lg flex flex-col justify-between">
              <div>
                <h2 className="text-lg font-semibold text-gray-800 mb-2">Validated</h2>
                <p className="text-4xl font-bold text-yellow-400">{stats.validated}</p>
              </div>
              <button
                onClick={() => navigate(`/admin/validated/${currentEvent.id}`)}
                className="mt-4 bg-gray-700 hover:bg-gray-600 text-white font-medium py-2 rounded-md shadow-md transition"
              >
                View Validated
              </button>
            </div>

            {/* INVALIDATED */}
            <div className="bg-white rounded-xl shadow-md p-6 hover:shadow-lg flex flex-col justify-between">
              <div>
                <h2 className="text-lg font-semibold text-gray-800 mb-2">Invalidated</h2>
                <p className="text-4xl font-bold text-yellow-400">{stats.invalidated}</p>
              </div>
              <button
                onClick={() => navigate(`/admin/invalidated/${currentEvent.id}`)}
                className="mt-4 bg-gray-700 hover:bg-gray-600 text-white font-medium py-2 rounded-md shadow-md transition"
              >
                View Invalidated
              </button>
            </div>

            {/* ATTENDANCE LOGS PER EVENT */}
            <div className="bg-white rounded-xl shadow-md p-6 hover:shadow-lg flex flex-col justify-between">
              <div>
                <h2 className="text-lg font-semibold text-gray-800 mb-2">Attendance Logs</h2>
                <p className="text-4xl font-bold text-yellow-500">{stats.attendanceTotal}</p>
              </div>
              <button
                onClick={() => navigate(`/admin/attendance-logs/${currentEvent.id}`)}
                className="mt-4 bg-gray-700 hover:bg-gray-600 text-white font-medium py-2 rounded-md shadow-md transition"
              >
                View Logs
              </button>
            </div>

            {/* SEND NOTIFICATIONS */}
            <div className="bg-white rounded-xl shadow-md p-6 hover:shadow-lg flex flex-col justify-between">
              <div>
                <h2 className="text-lg font-semibold text-gray-800 mb-2">Notifications</h2>
                <p className="text-sm text-gray-500">
                  Send announcements for this event only.
                </p>
              </div>
              <button
                onClick={() => navigate(`/admin/notifications/${currentEvent.id}`)}
                className="mt-4 bg-gray-700 hover:bg-gray-600 text-white font-medium py-2 rounded-md shadow-md transition"
              >
                Add Notifications
              </button>
            </div>

          </div>
        )}
      </div>
    </div>
  );
}
