// src/pages/EventFeedbacks.js
import React, { useEffect, useMemo, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  query,
  where,
  doc,
  getDoc,
  updateDoc,
} from "firebase/firestore";
import vader from "vader-sentiment";

const EVENT_Q_COLLECTION = "event_questions";
const USERS_COLLECTION = "users";

/* --------------------------- helpers --------------------------- */

const average = (nums) => {
  const vals = (Array.isArray(nums) ? nums : [])
    .map(Number)
    .filter((n) => !Number.isNaN(n));
  if (!vals.length) return 0;
  return vals.reduce((a, b) => a + b, 0) / vals.length;
};

/* Sentiment = comment only */
const analyzeCommentSentiment = (text = "") => {
  if (!text || typeof text !== "string") return "neutral";

  const lower = text.toLowerCase().trim();
  const spamWords = ["http://", "https://", "buy now", "promo", "free $$$", "visit my"];
  if (spamWords.some((w) => lower.includes(w))) return "spam";

  const noVowels = !/[aeiou]/i.test(lower);
  const longGibberish = /^[a-z]{15,}$/i.test(lower);
  if (noVowels || longGibberish) return "spam";

  try {
    const result = vader.SentimentIntensityAnalyzer.polarity_scores(text);
    if (result.compound >= 0.05) return "positive";
    if (result.compound <= -0.05) return "negative";
  } catch {}

  return "neutral";
};

const fmtDate = (ts) => {
  try {
    if (!ts) return "—";
    if (typeof ts?.toDate === "function") return ts.toDate().toLocaleString();
    if (ts?.seconds != null) return new Date(ts.seconds * 1000).toLocaleString();
    return String(ts);
  } catch {
    return "—";
  }
};

/* ---------------- UI small components ---------------- */

const Pill = ({ active, onClick, children }) => (
  <button
    onClick={onClick}
    className={
      "flex items-center gap-2 px-4 py-2 rounded-full text-sm font-medium transition " +
      (active ? "bg-blue-600 text-white shadow" : "bg-white text-gray-700 hover:bg-gray-100 border")
    }
  >
    {children}
  </button>
);

const CountBadge = ({ color, children }) => {
  const colors = {
    gray: "bg-gray-400 text-white",
    green: "bg-green-500 text-white",
    red: "bg-red-500 text-white",
    yellow: "bg-yellow-500 text-white",
  };
  return (
    <span className={`rounded-full px-2 py-0.5 text-xs font-semibold ${colors[color]}`}>
      {children}
    </span>
  );
};

/* --------------------------- Feedback Modal --------------------------- */

function FeedbackModal({ open, onClose, feedback = {}, questions = [], openEndedQuestions = [] }) {
  if (!open || !feedback) return null;

  const ratings = Array.isArray(feedback.ratings)
    ? feedback.ratings.map(Number)
    : [];

  const answers = feedback.answers
    ? Array.isArray(feedback.answers)
      ? feedback.answers
      : Object.values(feedback.answers)
    : [];

  const avg = Number(average(ratings || []).toFixed(2));
  const sentiment = analyzeCommentSentiment(feedback.comment).toLowerCase();

  const studentID = feedback.studentID ?? feedback.userId ?? "—";
  const name =
    feedback.userName ??
    feedback.displayName ??
    (feedback.anonymous ? "Anonymous" : feedback.userEmail ?? "User");

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50 p-4">
      <div className="bg-white rounded-xl shadow-xl max-w-3xl w-full overflow-auto max-h-[85vh]">

        <div className="flex items-center justify-between p-5 border-b">
          <h3 className="text-lg font-semibold">Feedback Detail</h3>
          <button onClick={onClose} className="text-gray-600 hover:text-gray-800">Close ✕</button>
        </div>

        <div className="p-6 space-y-4">

          <div className="flex items-center justify-between">
            <div>
              <div className="text-sm text-gray-500">Submitted</div>
              <div className="text-sm text-gray-700">{fmtDate(feedback.createdAt)}</div>
              <div className="text-xs text-gray-500 mt-1">
                Student ID: <span className="font-medium">{studentID}</span>
              </div>
              <div className="text-xs text-gray-500">
                Name: <span className="font-medium">{name}</span>
              </div>
            </div>

            <div className="text-right">
              <div className="text-sm text-gray-500">Average Rating</div>
              <div className="text-2xl font-bold text-gray-800">
                {avg} <span className="text-yellow-500">★</span>
              </div>
            </div>

            <div>
              <div className="text-sm text-gray-500">Sentiment</div>
              <div
                className={`px-3 py-1 rounded-full text-xs font-semibold ${
                  sentiment === "positive"
                    ? "bg-green-100 text-green-700"
                    : sentiment === "negative"
                    ? "bg-red-100 text-red-700"
                    : sentiment === "spam"
                    ? "bg-yellow-100 text-yellow-700"
                    : "bg-gray-100 text-gray-700"
                }`}
              >
                {sentiment}
              </div>
            </div>
          </div>

          {/* Ratings + Answers */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">

            <div className="bg-gray-50 p-4 rounded-md">
              <div className="text-sm font-medium text-gray-600 mb-3">Question Ratings</div>
              <ul className="space-y-2 text-sm text-gray-700">
                {questions.length ? (
                  questions.map((q, idx) => (
                    <li key={idx} className="flex items-center justify-between">
                      <div>
                        <span className="text-gray-500 mr-2">{idx + 1}.</span> {q}
                      </div>
                      <div className="font-semibold">{ratings[idx] ?? 0} ★</div>
                    </li>
                  ))
                ) : (
                  <div className="text-gray-500 italic">No rated questions available.</div>
                )}
              </ul>
            </div>

            <div className="bg-gray-50 p-4 rounded-md">
              <div className="text-sm font-medium text-gray-600 mb-2">Open-ended Q → A</div>
              {openEndedQuestions.length ? (
                openEndedQuestions.map((q, i) => (
                  <div key={i} className="p-3 bg-white rounded-md border mb-2">
                    <div className="text-xs text-gray-500 mb-1">
                      <strong>Q{i + 1}:</strong> {q}
                    </div>
                    <div>{answers[i] ?? <em className="text-gray-400">No answer</em>}</div>
                  </div>
                ))
              ) : (
                <div className="text-gray-500 italic">No open-ended questions available.</div>
              )}
            </div>

          </div>

          <div>
            <div className="text-sm font-medium text-gray-600 mb-1">Comment</div>
            <div className="bg-white p-3 rounded-md border text-gray-700">
              {feedback.comment || <span className="italic text-gray-400">No comment</span>}
            </div>
          </div>

          <div className="flex justify-end">
            <button onClick={onClose} className="px-4 py-2 rounded-md border bg-white text-gray-700">
              Close
            </button>
          </div>

        </div>
      </div>
    </div>
  );
}

/* --------------------------- PAGE --------------------------- */

export default function EventFeedbacks() {
  const { eventId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();

  const [eventName] = useState(location?.state?.eventName || "Event");
  const [feedbacks, setFeedbacks] = useState([]);
  const [questions, setQuestions] = useState([]);
  const [openEndedQuestions, setOpenEndedQuestions] = useState([]);
  const [active, setActive] = useState(
    new URLSearchParams(location.search).get("sentiment") || "all"
  );

  const [modalOpen, setModalOpen] = useState(false);
  const [selectedFeedback, setSelectedFeedback] = useState(null);

  /* Fetch questions */
  useEffect(() => {
    if (!eventId) return;
    (async () => {
      try {
        const snap = await getDoc(doc(db, EVENT_Q_COLLECTION, eventId));
        if (snap.exists()) {
          const data = snap.data();
          setQuestions(Array.isArray(data.questions) ? data.questions : []);
          setOpenEndedQuestions(Array.isArray(data.openEnded) ? data.openEnded : []);
        }
      } catch {}
    })();
  }, [eventId]);

  /* Fetch feedbacks */
  useEffect(() => {
    if (!eventId) return;

    const qRef = query(collection(db, "feedbacks"), where("eventId", "==", eventId));

    const unsub = onSnapshot(qRef, async (snap) => {
      const temp = [];
      const userIds = new Set();

      snap.docs.forEach((d) => {
        const raw = d.data();
        const fb = {
          id: d.id,
          ...raw,
          ratings: Array.isArray(raw.ratings) ? raw.ratings.map(Number) : [],
          answers: raw.answers
            ? Array.isArray(raw.answers)
              ? raw.answers
              : Object.values(raw.answers)
            : [],
        };
        temp.push(fb);
        if (raw.userId) userIds.add(raw.userId);
      });

      /* Merge user info */
      const userMap = {};
      await Promise.all(
        [...userIds].map(async (uid) => {
          try {
            const u = await getDoc(doc(db, USERS_COLLECTION, uid));
            if (u.exists()) {
              const ud = u.data();
              userMap[uid] = {
                displayName:
                  ud.displayName ?? `${ud.firstName ?? ""} ${ud.surname ?? ""}`.trim(),
                idNumber: ud.idNumber ?? ud.studentID ?? null,
              };
            }
          } catch {}
        })
      );

      /* Sentiment */
      temp.forEach((fb) => {
        if (fb.userId && userMap[fb.userId]) {
          fb.displayName = userMap[fb.userId].displayName;
          fb.studentID = userMap[fb.userId].idNumber;
          fb.userName = fb.displayName;
        }

        fb.sentiment = fb.comment ? analyzeCommentSentiment(fb.comment) : "neutral";

        try {
          updateDoc(doc(db, "feedbacks", fb.id), { sentiment: fb.sentiment });
        } catch {}
      });

      temp.sort((a, b) => {
        const aMs = a.createdAt?.toMillis?.() ?? 0;
        const bMs = b.createdAt?.toMillis?.() ?? 0;
        return bMs - aMs;
      });

      setFeedbacks(temp);
    });

    return () => unsub();
  }, [eventId]);

  /* enriched */
  const enriched = useMemo(
    () =>
      feedbacks.map((fb) => ({
        ...fb,
        _avg: Number(average(fb.ratings || []).toFixed(2)),
        _sentiment: analyzeCommentSentiment(fb.comment).toLowerCase(),
      })),
    [feedbacks]
  );

  const counts = useMemo(
    () => ({
      total: enriched.length,
      positive: enriched.filter((f) => f._sentiment === "positive").length,
      negative: enriched.filter((f) => f._sentiment === "negative").length,
      spam: enriched.filter((f) => f._sentiment === "spam").length,
    }),
    [enriched]
  );

  const visible = useMemo(() => {
    if (active === "all") return enriched;
    return enriched.filter((f) => f._sentiment === active);
  }, [active, enriched]);

  /* Update URL */
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    if (active === "all") params.delete("sentiment");
    else params.set("sentiment", active);
    navigate({ search: params.toString() }, { replace: true });
  }, [active]);

  /* Open modal */
  const openModalWith = (fb) => {
    setSelectedFeedback(fb);
    setModalOpen(true);
  };

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">

        {/* Header (Archive button removed) */}
        <h1 className="text-3xl font-semibold text-gray-800 mb-6">
          Feedbacks — <span className="text-blue-600">{eventName}</span>
        </h1>

        {/* Filters */}
        <div className="flex flex-wrap gap-3 mb-6">
          <Pill active={active === "all"} onClick={() => setActive("all")}>
            All <CountBadge color="gray">{counts.total}</CountBadge>
          </Pill>
          <Pill active={active === "positive"} onClick={() => setActive("positive")}>
            Positive <CountBadge color="green">{counts.positive}</CountBadge>
          </Pill>
          <Pill active={active === "negative"} onClick={() => setActive("negative")}>
            Negative <CountBadge color="red">{counts.negative}</CountBadge>
          </Pill>
          <Pill active={active === "spam"} onClick={() => setActive("spam")}>
            Spam <CountBadge color="yellow">{counts.spam}</CountBadge>
          </Pill>
        </div>

        {/* Table */}
        <div className="bg-white rounded-xl shadow p-4">
          <div className="overflow-x-auto">
            <table className="min-w-full text-left">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-3 text-sm font-semibold text-gray-600">Student ID</th>
                  <th className="px-4 py-3 text-sm font-semibold text-gray-600">Name</th>
                  <th className="px-4 py-3 text-sm font-semibold text-gray-600">Comment</th>
                  <th className="px-4 py-3 text-sm font-semibold text-gray-600 text-center">Sentiment</th>
                </tr>
              </thead>

              <tbody>
                {visible.length === 0 ? (
                  <tr>
                    <td colSpan="4" className="px-6 py-8 text-center text-gray-500">
                      No feedbacks found.
                    </td>
                  </tr>
                ) : (
                  visible.map((fb) => (
                    <tr
                      key={fb.id}
                      className="border-t hover:bg-gray-50 cursor-pointer"
                      onClick={() => openModalWith(fb)}
                    >
                      <td className="px-4 py-4 text-sm">{fb.studentID ?? "—"}</td>
                      <td className="px-4 py-4 text-sm">
                        {fb.anonymous ? <em>Anonymous</em> : fb.userName || "User"}
                      </td>
                      <td className="px-4 py-4 text-sm truncate max-w-[40ch]">
                        {fb.comment || "—"}
                      </td>
                      <td className="px-4 py-4 text-sm text-center">
                        <span
                          className={`inline-flex px-3 py-1 rounded-full text-xs font-medium ${
                            fb._sentiment === "positive"
                              ? "bg-green-100 text-green-700"
                              : fb._sentiment === "negative"
                              ? "bg-red-100 text-red-700"
                              : fb._sentiment === "spam"
                              ? "bg-yellow-100 text-yellow-700"
                              : "bg-gray-100 text-gray-700"
                          }`}
                        >
                          {fb._sentiment}
                        </span>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>

        {/* Modal */}
        <FeedbackModal
          open={modalOpen}
          onClose={() => {
            setModalOpen(false);
            setSelectedFeedback(null);
          }}
          feedback={selectedFeedback || {}}
          questions={questions}
          openEndedQuestions={openEndedQuestions}
        />
      </div>
    </div>
  );
}
