// src/pages/Dashboard.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  getCountFromServer,
  query,
  where,
  onSnapshot,
  limit,
  updateDoc,
  doc,
} from "firebase/firestore";
import Swal from "sweetalert2";

// Example icons (replace with your actual asset imports)
import eventIcon from "../assets/images/event.png";
import feedbackIcon from "../assets/images/feedback.png";
import checkinIcon from "../assets/images/checkin.png";
import validIcon from "../assets/images/valid.png";
import invalidIcon from "../assets/images/invalid.png";
import notifyIcon from "../assets/images/notify.png";

export default function Dashboard() {
  const navigate = useNavigate();

  const [stats, setStats] = useState({
    feedbacks: 0,
    checkins: 0,
    valid: 0,
    invalid: 0,
    events: 0,
  });

  const [currentEvent, setCurrentEvent] = useState(null);
  const [toggling, setToggling] = useState(false);

  // Counts
  useEffect(() => {
    (async () => {
      try {
        const feedbackSnap = await getCountFromServer(collection(db, "feedbacks"));
        const eventsSnap = await getCountFromServer(collection(db, "events"));
        const checkinSnap = await getCountFromServer(collection(db, "checkins"));
        const validSnap = await getCountFromServer(
          query(collection(db, "checkins"), where("valid", "==", true))
        );
        const invalidSnap = await getCountFromServer(
          query(collection(db, "checkins"), where("valid", "==", false))
        );

        setStats({
          feedbacks: feedbackSnap.data().count,
          events: eventsSnap.data().count,
          checkins: checkinSnap.data().count,
          valid: validSnap.data().count,
          invalid: invalidSnap.data().count,
        });
      } catch {
        setStats({ feedbacks: 0, checkins: 0, valid: 0, invalid: 0, events: 0 });
      }
    })();
  }, []);

  // Live: current event
  useEffect(() => {
    const qRef = query(collection(db, "events"), where("isCurrent", "==", true), limit(1));
    const unsub = onSnapshot(qRef, (snap) => {
      if (snap.empty) {
        setCurrentEvent(null);
      } else {
        const d = snap.docs[0];
        setCurrentEvent({ id: d.id, ...d.data() });
      }
    });
    return () => unsub();
  }, []);

  const handleToggleFeedback = async () => {
    if (!currentEvent?.id) return;
    const next = !Boolean(currentEvent.feedbackOpen);
    setToggling(true);
    try {
      await updateDoc(doc(db, "events", currentEvent.id), {
        feedbackOpen: next,
      });
      Swal.fire({
        icon: "success",
        title: next ? "Feedback Form Opened" : "Feedback Form Closed",
        timer: 1400,
        showConfirmButton: false,
      });
    } catch (e) {
      Swal.fire({
        icon: "error",
        title: "Failed to update",
        text: e?.message || "",
      });
    } finally {
      setToggling(false);
    }
  };

  return (
    <div className="flex h-screen bg-[#0d1736] text-white font-poppins">
      {/* Sidebar (dark theme) */}
      <Sidebar />

      {/* Dashboard Content */}
      <div className="flex-1 p-10 overflow-y-auto">
        <h1 className="text-3xl font-semibold mb-10">
          Welcome back, <span className="text-yellow-400">Admin!</span>
        </h1>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {/* Current Event card */}
          <div className="bg-[#1c2239] rounded-xl shadow-md p-6 transition hover:shadow-lg">
            <div className="flex items-center gap-3 mb-3">
              <img
                src={eventIcon}
                alt="Event"
                className="w-6 h-6"
                style={{
                  filter:
                    "invert(74%) sepia(93%) saturate(747%) hue-rotate(1deg) brightness(103%) contrast(102%)",
                }}
              />
              <h2 className="text-lg font-medium text-yellow-400">Current Event</h2>
            </div>

            {currentEvent ? (
              <>
                <p className="text-xl font-bold text-white">
                  {currentEvent.title || "Untitled"}
                </p>
                <p className="text-sm text-gray-300 mt-1">
                  {currentEvent.location ? `@ ${currentEvent.location}` : "Location: TBA"}
                </p>
                <p className="text-xs text-gray-400 mt-1">
                  {currentEvent.date || "Date: TBA"}
                </p>

                {/* Feedback Open/Close Switch */}
                <div className="mt-5">
                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm font-medium text-gray-200">
                        Feedback Form
                      </p>
                      <p className="text-xs text-gray-400">
                        Toggle to manually {currentEvent.feedbackOpen ? "close" : "open"} the mobile feedback form.
                      </p>
                    </div>

                    {/* Switch */}
                    <button
                      onClick={handleToggleFeedback}
                      disabled={toggling}
                      className={
                        "relative inline-flex h-6 w-11 items-center rounded-full transition " +
                        (currentEvent.feedbackOpen ? "bg-yellow-400" : "bg-gray-500") +
                        (toggling ? " opacity-70 cursor-not-allowed" : " hover:brightness-95")
                      }
                      title={currentEvent.feedbackOpen ? "Close feedback" : "Open feedback"}
                    >
                      <span
                        className={
                          "inline-block h-5 w-5 transform rounded-full bg-white shadow transition " +
                          (currentEvent.feedbackOpen ? "translate-x-5" : "translate-x-1")
                        }
                      />
                    </button>
                  </div>
                </div>
              </>
            ) : (
              <p className="text-gray-400">
                No current event selected. Set one in{" "}
                <span className="font-semibold text-yellow-400">Manage Events</span>.
              </p>
            )}
          </div>

          {/* Total Feedback */}
          <div className="bg-[#1c2239] rounded-xl shadow-md p-6 transition hover:shadow-lg">
            <div className="flex items-center gap-3 mb-3">
              <img
                src={feedbackIcon}
                alt="Feedback"
                className="w-6 h-6"
                style={{
                  filter:
                    "invert(74%) sepia(93%) saturate(747%) hue-rotate(1deg) brightness(103%) contrast(102%)",
                }}
              />
              <h2 className="text-lg font-medium text-yellow-400">Total Feedback</h2>
            </div>
            <p className="text-4xl font-bold text-white">{stats.feedbacks}</p>
          </div>

          {/* Total Check-ins */}
          <div className="bg-[#1c2239] rounded-xl shadow-md p-6 transition hover:shadow-lg">
            <div className="flex items-center gap-3 mb-3">
              <img
                src={checkinIcon}
                alt="Check-ins"
                className="w-6 h-6"
                style={{
                  filter:
                    "invert(74%) sepia(93%) saturate(747%) hue-rotate(1deg) brightness(103%) contrast(102%)",
                }}
              />
              <h2 className="text-lg font-medium text-yellow-400">Total Check-ins</h2>
            </div>
            <p className="text-4xl font-bold text-white">{stats.checkins}</p>
          </div>

          {/* Valid Check-ins */}
          <div className="bg-[#1c2239] rounded-xl shadow-md p-6 transition hover:shadow-lg">
            <div className="flex items-center gap-3 mb-3">
              <img
                src={validIcon}
                alt="Valid"
                className="w-6 h-6"
                style={{
                  filter:
                    "invert(74%) sepia(93%) saturate(747%) hue-rotate(1deg) brightness(103%) contrast(102%)",
                }}
              />
              <h2 className="text-lg font-medium text-yellow-400">Valid Check-ins</h2>
            </div>
            <p className="text-4xl font-bold text-white">{stats.valid}</p>
          </div>

          {/* Invalid Check-ins */}
          <div className="bg-[#1c2239] rounded-xl shadow-md p-6 transition hover:shadow-lg">
            <div className="flex items-center gap-3 mb-3">
              <img
                src={invalidIcon}
                alt="Invalid"
                className="w-6 h-6"
                style={{
                  filter:
                    "invert(74%) sepia(93%) saturate(747%) hue-rotate(1deg) brightness(103%) contrast(102%)",
                }}
              />
              <h2 className="text-lg font-medium text-yellow-400">Invalid Check-ins</h2>
            </div>
            <p className="text-4xl font-bold text-white">{stats.invalid}</p>
          </div>

          {/* Notifications quick action */}
          <div className="bg-[#1c2239] rounded-xl shadow-md p-6 transition hover:shadow-lg">
            <div className="flex items-center gap-3 mb-3">
              <img
                src={notifyIcon}
                alt="Notify"
                className="w-6 h-6"
                style={{
                  filter:
                    "invert(74%) sepia(93%) saturate(747%) hue-rotate(1deg) brightness(103%) contrast(102%)",
                }}
              />
              <h2 className="text-lg font-medium text-yellow-400">Notifications</h2>
            </div>
            <button
              className="mt-2 w-full bg-yellow-400 text-black font-semibold py-2 rounded-md hover:bg-yellow-500 transition"
              onClick={() => navigate("/admin/notifications")}
            >
              Add Notifications
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
