import React, { useEffect, useState, useMemo } from "react";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  query,
  where,
  onSnapshot,
  getDoc,
  getDocs,
  doc,
  orderBy,
  limit,
  addDoc,
} from "firebase/firestore";

export default function Invalidated() {
  const [invalidatedUsers, setInvalidatedUsers] = useState([]);
  const [search, setSearch] = useState("");
  const [filtered, setFiltered] = useState([]);
  const [currentEvent, setCurrentEvent] = useState(null);

  // ‚úÖ Automatically create collections & test data if not found
  useEffect(() => {
    async function ensureFirestoreSetup() {
      try {
        // üß© Create test event if none exist
        const eventsSnap = await getDocs(collection(db, "events"));
        if (eventsSnap.empty) {
          await addDoc(collection(db, "events"), {
            title: "Brent's Test",
            date: "2025-10-06",
            location: "Main Hall",
            isCurrent: true,
          });
          console.log("‚úÖ Created default event 'Brent's Test'");
        }

        // üß© Create test user if none exist
        const usersSnap = await getDocs(collection(db, "users"));
        if (usersSnap.empty) {
          await addDoc(collection(db, "users"), {
            idNumber: "2025-001",
            email: "brent@example.com",
            fullName: "Brent Example",
          });
          await addDoc(collection(db, "users"), {
            idNumber: "2025-002",
            email: "jane@example.com",
            fullName: "Jane Doe",
          });
          console.log("‚úÖ Created default users");
        }

        // üß© Create sample invalidated attendance if empty
        const liveSnap = await getDocs(collection(db, "liveAttendance"));
        if (liveSnap.empty) {
          await addDoc(collection(db, "liveAttendance"), {
            studentID: "2025-002",
            email: "jane@example.com",
            eventTitle: "Brent's Test",
            status: "invalidated",
            timestamp: new Date(),
          });
          console.log("‚úÖ Created sample invalidated record");
        }
      } catch (error) {
        console.error("‚ùå Firestore setup error:", error);
      }
    }

    ensureFirestoreSetup();
  }, []);

  // ‚úÖ Fetch the current event
  useEffect(() => {
    const qRef = query(
      collection(db, "events"),
      where("isCurrent", "==", true),
      limit(1)
    );

    const unsub = onSnapshot(qRef, (snap) => {
      if (!snap.empty) {
        const event = { id: snap.docs[0].id, ...snap.docs[0].data() };
        setCurrentEvent(event);
      } else {
        setCurrentEvent(null);
      }
    });

    return () => unsub();
  }, []);

  // ‚úÖ Real-time fetch of invalidated users for the current event (unique per studentID)
  useEffect(() => {
    if (!currentEvent?.title) return;

    const qRef = query(
      collection(db, "liveAttendance"),
      where("eventTitle", "==", currentEvent.title),
      where("status", "==", "invalidated"),
      orderBy("timestamp", "desc")
    );

    const unsub = onSnapshot(qRef, async (snap) => {
      const mapByStudent = new Map();

      for (const docSnap of snap.docs) {
        const data = docSnap.data();
        const studentID = data.studentID || "‚Äî";

        // Only keep the most recent record per student
        if (!mapByStudent.has(studentID)) {
          mapByStudent.set(studentID, { id: docSnap.id, ...data });
        }
      }

      // Convert map values to array
      const uniqueList = Array.from(mapByStudent.values());

      // Fetch missing student ID if needed
      const list = await Promise.all(
        uniqueList.map(async (d) => {
          let studentID = d.studentID || "‚Äî";
          if ((!studentID || studentID === "‚Äî") && d.userId) {
            try {
              const userDoc = await getDoc(doc(db, "users", d.userId));
              if (userDoc.exists()) {
                studentID = userDoc.data().idNumber || "‚Äî";
              }
            } catch {
              studentID = "‚Äî";
            }
          }
          return { id: d.id, ...d, studentID };
        })
      );

      setInvalidatedUsers(list);
    });

    return () => unsub();
  }, [currentEvent]);

  // ‚úÖ Format timestamp
  const fmtDate = (ts) => {
    if (!ts) return "‚Äî";
    const date = ts?.seconds ? new Date(ts.seconds * 1000) : new Date(ts);
    return date.toLocaleString("en-US", {
      month: "short",
      day: "numeric",
      year: "numeric",
      hour: "numeric",
      minute: "2-digit",
      hour12: true,
    });
  };

  // ‚úÖ Search filter
  useEffect(() => {
    const term = search.toLowerCase();
    let results = invalidatedUsers;

    if (term) {
      results = results.filter(
        (u) =>
          u.studentID?.toString().toLowerCase().includes(term) ||
          u.email?.toLowerCase().includes(term)
      );
    }

    setFiltered(results);
  }, [invalidatedUsers, search]);

  // ‚úÖ Counter
  const count = useMemo(() => filtered.length, [filtered]);

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-3xl font-semibold text-gray-800">
            Invalidated{" "}
            {currentEvent?.title && (
              <span className="text-yellow-500 font-semibold">
                ‚Äì ‚Äú{currentEvent.title}‚Äù
              </span>
            )}
          </h1>
          <p className="text-red-600 font-medium">
            Currently Invalidated:{" "}
            <span className="text-gray-800 font-semibold">{count}</span>
          </p>
        </div>

        {/* Search Bar */}
        <div className="flex mb-6">
          <input
            type="text"
            placeholder="Search by Student ID or Email..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-yellow-400 focus:outline-none"
          />
        </div>

        {/* Invalidated Table */}
        <div className="bg-white rounded-xl shadow overflow-hidden">
          <table className="min-w-full table-auto border-collapse">
            <thead className="bg-gray-50 text-gray-700 text-sm font-semibold">
              <tr>
                <th className="px-6 py-3 text-left">Student ID</th>
                <th className="px-6 py-3 text-left">Email</th>
                <th className="px-6 py-3 text-left">Date &amp; Time</th>
                <th className="px-6 py-3 text-left">Status</th>
              </tr>
            </thead>
            <tbody>
              {filtered.length > 0 ? (
                filtered.map((user) => (
                  <tr
                    key={user.id}
                    className="border-t hover:bg-gray-50 transition"
                  >
                    <td className="px-6 py-3 text-sm text-gray-700">
                      {user.studentID}
                    </td>
                    <td className="px-6 py-3 text-sm text-gray-700">
                      {user.email || "‚Äî"}
                    </td>
                    <td className="px-6 py-3 text-sm text-gray-700">
                      {fmtDate(user.timestamp)}
                    </td>
                    <td className="px-6 py-3">
                      <span className="px-3 py-1 text-xs font-semibold rounded-full bg-red-100 text-red-700">
                        INVALIDATED
                      </span>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="4"
                    className="px-6 py-10 text-center text-gray-500 italic"
                  >
                    {currentEvent
                      ? "No currently invalidated users."
                      : "No active event selected."}
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        <p className="text-xs text-gray-400 mt-4">
          * Updates live ‚Äî users appear instantly when invalidated.
        </p>
      </div>
    </div>
  );
}
