import React, { useState } from "react";
import { db } from "../firebase/firebaseConfig";
import { collection, addDoc, serverTimestamp } from "firebase/firestore";
import Swal from "sweetalert2";
import Sidebar from "../components/Sidebar";

function AddEvent() {
  const [title, setTitle] = useState("");
  const [location, setLocation] = useState("");
  const [date, setDate] = useState("");

  const handleAddEvent = async (e) => {
    e.preventDefault();

    if (!title || !location || !date) {
      Swal.fire({
        icon: "warning",
        title: "Missing fields",
        text: "Please fill out all fields.",
        timer: 2000,
        showConfirmButton: false,
      });
      return;
    }

    try {
      await addDoc(collection(db, "events"), {
        title,
        location,
        date,
        createdAt: serverTimestamp(),
      });

      Swal.fire({
        icon: "success",
        title: "Event Added!",
        text: "Your event has been successfully created.",
        timer: 2000,
        showConfirmButton: false,
      });

      // Reset form
      setTitle("");
      setLocation("");
      setDate("");
    } catch (error) {
      console.error("Error adding event:", error);
      Swal.fire({
        icon: "error",
        title: "Error",
        text: "Could not add event. Try again later.",
      });
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 p-8">
        <h2 className="text-3xl font-cinzel font-bold mb-6 text-gray-800">
          Add Event
        </h2>

        {/* Form Container */}
        <div className="bg-white shadow-lg rounded-lg p-6 max-w-lg">
          <form onSubmit={handleAddEvent} className="space-y-4">
            {/* Title */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Event Title
              </label>
              <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Enter event title"
                className="w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {/* Location */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Location
              </label>
              <input
                type="text"
                value={location}
                onChange={(e) => setLocation(e.target.value)}
                placeholder="Enter event location"
                className="w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {/* Date */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Date
              </label>
              <input
                type="date"
                value={date}
                onChange={(e) => setDate(e.target.value)}
                className="w-full p-3 border rounded-md focus:ring-2 focus:ring-blue-500"
              />
            </div>

            {/* Submit Button */}
            <button
              type="submit"
              className="w-full bg-blue-600 text-white font-bold py-3 rounded-md hover:bg-blue-700 transition"
            >
              Add Event
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default AddEvent;
