import React, { useState } from "react";
import { View, Text, TextInput, Pressable, ScrollView, ActivityIndicator } from "react-native";
import { addDoc, collection, serverTimestamp } from "firebase/firestore";
import { db } from "../src/firebase";
import { SafeAreaView, useSafeAreaInsets } from "react-native-safe-area-context";
import { ALERT_TYPE, AlertNotificationRoot, Dialog } from "react-native-alert-notification";
import { router } from "expo-router";

const C = {
  bg: "#1b1f38",
  accent: "#FFD700",
  muted: "#b0b6d3",
  button: "#FFD700",
  buttonText: "#1b1f38",
  border: "rgba(255, 255, 255, 0.15)",
};

export default function AddEvent() {
  const insets = useSafeAreaInsets();
  const [title, setTitle] = useState("");
  const [location, setLocation] = useState("");
  const [latitude, setLatitude] = useState("");
  const [longitude, setLongitude] = useState("");
  const [radius, setRadius] = useState("");
  const [startAt, setStartAt] = useState("");
  const [endAt, setEndAt] = useState("");
  const [loading, setLoading] = useState(false);

  const handleAddEvent = async () => {
    if (!title || !location || !latitude || !longitude || !radius) {
      Dialog.show({
        type: ALERT_TYPE.WARNING,
        title: "Missing Fields",
        textBody: "Please fill in all fields before adding the event.",
        button: "OK",
      });
      return;
    }

    setLoading(true);
    try {
      await addDoc(collection(db, "events"), {
        title,
        location,
        latitude: parseFloat(latitude),
        longitude: parseFloat(longitude),
        radius: parseFloat(radius),
        startAt: startAt ? new Date(startAt) : null,
        endAt: endAt ? new Date(endAt) : null,
        createdAt: serverTimestamp(),
        isCurrent: true, // mark as active/current
      });

      Dialog.show({
        type: ALERT_TYPE.SUCCESS,
        title: "Event Added",
        textBody: "Your event has been successfully created!",
        button: "OK",
      });

      setTitle("");
      setLocation("");
      setLatitude("");
      setLongitude("");
      setRadius("");
      setStartAt("");
      setEndAt("");
    } catch (error) {
      console.error(error);
      Dialog.show({
        type: ALERT_TYPE.DANGER,
        title: "Error",
        textBody: "Something went wrong while adding the event.",
        button: "OK",
      });
    }
    setLoading(false);
  };

  return (
    <AlertNotificationRoot>
      <SafeAreaView
        style={{
          flex: 1,
          backgroundColor: C.bg,
          paddingTop: Math.max(insets.top, 20),
        }}
      >
        <ScrollView
          contentContainerStyle={{
            paddingHorizontal: 20,
            paddingBottom: 100,
          }}
          showsVerticalScrollIndicator={false}
        >
          <Text
            style={{
              color: "#fff",
              fontFamily: "Poppins_700Bold",
              fontSize: 22,
              textAlign: "center",
              marginBottom: 20,
            }}
          >
            Add New Event
          </Text>

          {/* Input fields */}
          <View style={{ gap: 12 }}>
            <TextInput
              placeholder="Event Title"
              placeholderTextColor={C.muted}
              value={title}
              onChangeText={setTitle}
              style={styles.input}
            />
            <TextInput
              placeholder="Event Location Name"
              placeholderTextColor={C.muted}
              value={location}
              onChangeText={setLocation}
              style={styles.input}
            />
            <TextInput
              placeholder="Latitude (e.g. 8.508802)"
              placeholderTextColor={C.muted}
              value={latitude}
              onChangeText={setLatitude}
              keyboardType="numeric"
              style={styles.input}
            />
            <TextInput
              placeholder="Longitude (e.g. 124.602974)"
              placeholderTextColor={C.muted}
              value={longitude}
              onChangeText={setLongitude}
              keyboardType="numeric"
              style={styles.input}
            />
            <TextInput
              placeholder="Validation Radius (meters)"
              placeholderTextColor={C.muted}
              value={radius}
              onChangeText={setRadius}
              keyboardType="numeric"
              style={styles.input}
            />
            <TextInput
              placeholder="Start Date & Time (YYYY-MM-DD HH:mm)"
              placeholderTextColor={C.muted}
              value={startAt}
              onChangeText={setStartAt}
              style={styles.input}
            />
            <TextInput
              placeholder="End Date & Time (YYYY-MM-DD HH:mm)"
              placeholderTextColor={C.muted}
              value={endAt}
              onChangeText={setEndAt}
              style={styles.input}
            />
          </View>

          {/* Submit Button */}
          <Pressable
            onPress={handleAddEvent}
            disabled={loading}
            style={{
              backgroundColor: C.button,
              paddingVertical: 14,
              borderRadius: 12,
              alignItems: "center",
              marginTop: 30,
              shadowColor: "#000",
              shadowOpacity: 0.3,
              shadowOffset: { width: 0, height: 3 },
              shadowRadius: 5,
              elevation: 4,
            }}
          >
            {loading ? (
              <ActivityIndicator color={C.buttonText} />
            ) : (
              <Text
                style={{
                  color: C.buttonText,
                  fontFamily: "Poppins_700Bold",
                }}
              >
                ADD EVENT
              </Text>
            )}
          </Pressable>
        </ScrollView>
      </SafeAreaView>
    </AlertNotificationRoot>
  );
}

const styles = {
  input: {
    backgroundColor: "rgba(255,255,255,0.05)",
    color: "#fff",
    fontFamily: "Poppins_400Regular",
    borderRadius: 10,
    paddingVertical: 12,
    paddingHorizontal: 16,
    borderWidth: 1,
    borderColor: "rgba(255,255,255,0.15)",
  },
};
