// src/pages/ArchivedEvents.js
import React, { useEffect, useState } from "react";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  deleteDoc,
  addDoc,
  doc,
} from "firebase/firestore";
import Sidebar from "../components/Sidebar";

function ArchivedEvents() {
  const [archived, setArchived] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [modalType, setModalType] = useState(""); // "restore" or "delete"
  const [notification, setNotification] = useState(null);

  useEffect(() => {
    const unsubscribe = onSnapshot(
      collection(db, "archived_events"),
      (snapshot) => {
        setArchived(
          snapshot.docs.map((d) => ({
            id: d.id,
            ...d.data(),
          }))
        );
      }
    );
    return () => unsubscribe();
  }, []);

  const showNotification = (msg) => {
    setNotification(msg);
    setTimeout(() => setNotification(null), 3000);
  };

  /* ------------------ RESTORE EVENT ------------------ */
  const restoreEvent = async () => {
    if (!selectedEvent) return;

    try {
      await addDoc(collection(db, "events"), {
        title: selectedEvent.title,
        location: selectedEvent.location,
        date: selectedEvent.date,
        createdAt: selectedEvent.createdAt || new Date(),
        isCurrent: false,
      });

      await deleteDoc(doc(db, "archived_events", selectedEvent.id));

      showNotification("Event restored successfully!");
      setSelectedEvent(null);
      setModalType("");
    } catch (err) {
      console.error(err);
    }
  };

  /* ------------------ DELETE PERMANENTLY ------------------ */
  const deleteEvent = async () => {
    if (!selectedEvent) return;

    try {
      await deleteDoc(doc(db, "archived_events", selectedEvent.id));

      showNotification("Event deleted permanently!");
      setSelectedEvent(null);
      setModalType("");
    } catch (err) {
      console.error(err);
    }
  };

  /* ------------------ OPEN MODAL ------------------ */
  const openModal = (event, type) => {
    setSelectedEvent(event);
    setModalType(type); // "restore" or "delete"
  };

  return (
    <div className="flex min-h-screen bg-gray-100 font-poppins">
      <Sidebar />

      {/* MAIN CONTENT */}
      <div className="flex-1 p-4 md:p-8 lg:p-10 overflow-y-auto relative">

        <h2 className="text-2xl md:text-3xl font-semibold text-gray-800 mb-6">
          Archived Events
        </h2>

        {/* TOAST */}
        {notification && (
          <div className="fixed top-6 right-6 bg-gray-900 text-white px-4 py-3 rounded-lg shadow-lg text-sm font-medium animate-fade-in-out z-50">
            {notification}
          </div>
        )}

        {/* TABLE */}
        <div className="bg-white shadow-xl rounded-2xl border border-gray-200 overflow-x-auto">
          <table className="min-w-full text-left text-sm md:text-base">
            <thead className="bg-gray-800 text-gray-100">
              <tr>
                <th className="px-4 md:px-6 py-3">Title</th>
                <th className="px-4 md:px-6 py-3">Location</th>
                <th className="px-4 md:px-6 py-3">Date</th>
                <th className="px-4 md:px-6 py-3 text-center">Actions</th>
              </tr>
            </thead>

            <tbody>
              {archived.length > 0 ? (
                archived.map((event) => (
                  <tr
                    key={event.id}
                    className="border-b border-gray-200 hover:bg-gray-50 transition"
                  >
                    <td className="px-4 md:px-6 py-4 text-gray-800">
                      {event.title}
                    </td>

                    <td className="px-4 md:px-6 py-4 text-gray-700">
                      {event.location}
                    </td>

                    <td className="px-4 md:px-6 py-4 text-gray-700">
                      {event.date}
                    </td>

                    <td className="px-4 md:px-6 py-4">
                      <div className="flex flex-wrap justify-center gap-2">

                        {/* RESTORE */}
                        <button
                          className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded-md text-xs md:text-sm transition"
                          onClick={() => openModal(event, "restore")}
                        >
                          Restore
                        </button>

                        {/* DELETE PERMANENT */}
                        <button
                          className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded-md text-xs md:text-sm transition"
                          onClick={() => openModal(event, "delete")}
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="4"
                    className="px-6 py-8 text-center text-gray-500 bg-white"
                  >
                    No archived events found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* MODAL */}
      {modalType && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 backdrop-blur-sm z-50">
          <div className="bg-white rounded-xl shadow-2xl w-80 md:w-96 p-6 text-center">
            <h3 className="text-xl font-semibold text-gray-800 mb-3">
              {modalType === "restore"
                ? "Restore Event"
                : "Delete Event Permanently"}
            </h3>

            <p className="text-gray-600 mb-6 text-sm md:text-base">
              {modalType === "restore" ? (
                <>
                  Are you sure you want to restore{" "}
                  <span className="font-semibold text-gray-900">
                    “{selectedEvent?.title}”
                  </span>{" "}
                  back to active events?
                </>
              ) : (
                <>
                  This action cannot be undone. Delete{" "}
                  <span className="font-semibold text-gray-900">
                    “{selectedEvent?.title}”
                  </span>{" "}
                  permanently?
                </>
              )}
            </p>

            <div className="flex justify-center gap-4">
              <button
                onClick={modalType === "restore" ? restoreEvent : deleteEvent}
                className={`${
                  modalType === "restore"
                    ? "bg-green-500 hover:bg-green-600"
                    : "bg-red-600 hover:bg-red-700"
                } text-white font-medium px-4 py-2 rounded-md transition`}
              >
                {modalType === "restore" ? "Restore" : "Delete"}
              </button>

              <button
                onClick={() => setModalType("")}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-medium px-4 py-2 rounded-md transition"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ArchivedEvents;
