import React, { useEffect, useState } from "react";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  addDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";
import Swal from "sweetalert2";
import Sidebar from "../components/Sidebar";

function ArchivedEvents() {
  const [archivedEvents, setArchivedEvents] = useState([]);

  // 🔄 Fetch archived events
  useEffect(() => {
    const unsubscribe = onSnapshot(
      collection(db, "archived_events"),
      (snapshot) => {
        const data = snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setArchivedEvents(data);
      }
    );
    return () => unsubscribe();
  }, []);

  // ♻️ Unarchive event
  const handleUnarchive = async (id, eventData) => {
    const confirm = await Swal.fire({
      title: "Restore this event?",
      text: "This will move the event back to Active Events.",
      icon: "question",
      showCancelButton: true,
      confirmButtonColor: "#1abc9c",
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Yes, restore it!",
    });

    if (confirm.isConfirmed) {
      try {
        await addDoc(collection(db, "events"), {
          ...eventData,
          restoredAt: new Date(),
        });

        await deleteDoc(doc(db, "archived_events", id));

        Swal.fire({
          icon: "success",
          title: "Restored!",
          text: "Event has been moved back.",
          timer: 2000,
          showConfirmButton: false,
        });
      } catch (error) {
        console.error("Error unarchiving:", error);
        Swal.fire({
          icon: "error",
          title: "Error",
          text: "Could not restore event.",
        });
      }
    }
  };

  // ❌ Delete event permanently
  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "Delete this event?",
      text: "This action cannot be undone.",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#e74c3c",
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Yes, delete it!",
    });

    if (confirm.isConfirmed) {
      try {
        await deleteDoc(doc(db, "archived_events", id));

        Swal.fire({
          icon: "success",
          title: "Deleted!",
          text: "Event has been permanently deleted.",
          timer: 2000,
          showConfirmButton: false,
        });
      } catch (error) {
        console.error("Error deleting:", error);
        Swal.fire({
          icon: "error",
          title: "Error",
          text: "Could not delete event.",
        });
      }
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 p-8 overflow-x-auto">
        <h2 className="text-3xl font-cinzel font-bold mb-6 text-gray-800">
          Archived Events
        </h2>

        {/* Table */}
        <div className="overflow-x-auto bg-white shadow-lg rounded-lg">
          <table className="min-w-full table-auto text-left border-collapse">
            <thead className="bg-indigo-600 text-white">
              <tr>
                <th className="px-6 py-3 text-sm font-semibold">Title</th>
                <th className="px-6 py-3 text-sm font-semibold">Location</th>
                <th className="px-6 py-3 text-sm font-semibold">Date</th>
                <th className="px-6 py-3 text-sm font-semibold text-center">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody>
              {archivedEvents.length > 0 ? (
                archivedEvents.map((event) => (
                  <tr
                    key={event.id}
                    className="border-b hover:bg-gray-50 transition"
                  >
                    <td className="px-6 py-4">{event.title}</td>
                    <td className="px-6 py-4">{event.location}</td>
                    <td className="px-6 py-4">{event.date}</td>
                    <td className="px-6 py-4 flex gap-2 justify-center">
                      <button
                        className="bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded-md text-sm"
                        onClick={() => handleUnarchive(event.id, event)}
                      >
                        Restore
                      </button>
                      <button
                        className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-md text-sm"
                        onClick={() => handleDelete(event.id)}
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="4"
                    className="px-6 py-8 text-center text-gray-500"
                  >
                    No archived events available.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}

export default ArchivedEvents;
