import React, { useEffect, useMemo, useState } from "react";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import { collection, onSnapshot, orderBy, query } from "firebase/firestore";

export default function AttendanceLogs() {
  const [logs, setLogs] = useState([]);
  const [search, setSearch] = useState("");
  const [filtered, setFiltered] = useState([]);

  // ✅ Real-time fetch from mobile app's "attendanceLogs" collection
  useEffect(() => {
    const qRef = query(collection(db, "attendanceLogs"), orderBy("timestamp", "desc"));
    const unsub = onSnapshot(qRef, (snap) => {
      const list = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
      setLogs(list);
    });
    return () => unsub();
  }, []);

  // ✅ Filter search (by user ID, event title, or status)
  useEffect(() => {
    const term = search.toLowerCase();
    setFiltered(
      logs.filter(
        (l) =>
          l.userId?.toLowerCase().includes(term) ||
          l.eventTitle?.toLowerCase().includes(term) ||
          l.status?.toLowerCase().includes(term)
      )
    );
  }, [search, logs]);

  // ✅ Count summary
  const counts = useMemo(() => {
    const total = logs.length;
    const valid = logs.filter((l) => l.status === "validated").length;
    const invalid = logs.filter((l) => l.status !== "validated").length;
    return { total, valid, invalid };
  }, [logs]);

  // ✅ Format Firestore timestamps
  const fmtDate = (ts) => {
    try {
      if (!ts) return "—";
      const date =
        ts?.seconds != null ? new Date(ts.seconds * 1000) : new Date(ts);
      return date.toLocaleString("en-US", {
        year: "numeric",
        month: "long",
        day: "numeric",
        hour: "numeric",
        minute: "2-digit",
        hour12: true,
      });
    } catch {
      return "—";
    }
  };

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">
        {/* ✅ Header with Counts */}
        <div className="flex items-center justify-between mb-6">
          <h1 className="text-3xl font-semibold text-gray-800">
            Attendance Logs
          </h1>
          <div className="text-right text-sm text-gray-600">
            <div>
              Total:{" "}
              <span className="font-semibold text-gray-800">
                {counts.total}
              </span>
            </div>
            <div className="text-green-600">
              Validated: {counts.valid}
            </div>
            <div className="text-red-600">
              Invalidated: {counts.invalid}
            </div>
          </div>
        </div>

        {/* ✅ Search Bar */}
        <div className="mb-6">
          <input
            type="text"
            placeholder="Search by User ID, Event, or Status..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="w-full px-4 py-3 rounded-lg border border-gray-300 focus:ring-2 focus:ring-yellow-400 focus:outline-none"
          />
        </div>

        {/* ✅ Logs Table */}
        <div className="bg-white rounded-xl shadow overflow-hidden">
          <table className="min-w-full table-auto border-collapse">
            <thead className="bg-gray-50 text-gray-700 text-sm font-semibold">
              <tr>
                <th className="px-6 py-3 text-left">User ID</th>
                <th className="px-6 py-3 text-left">Event Title</th>
                <th className="px-6 py-3 text-left">Status</th>
                <th className="px-6 py-3 text-left">Distance (m)</th>
                <th className="px-6 py-3 text-left">Date</th>
              </tr>
            </thead>
            <tbody>
              {filtered.length > 0 ? (
                filtered.map((log) => (
                  <tr
                    key={log.id}
                    className="border-t hover:bg-gray-50 transition"
                  >
                    <td className="px-6 py-3 text-sm text-gray-700">
                      {log.userId || "—"}
                    </td>
                    <td className="px-6 py-3 text-sm font-medium text-gray-800">
                      {log.eventTitle || "Unknown Event"}
                    </td>
                    <td className="px-6 py-3">
                      <span
                        className={`px-3 py-1 text-xs font-semibold rounded-full ${
                          log.status === "validated"
                            ? "bg-green-100 text-green-700"
                            : "bg-red-100 text-red-700"
                        }`}
                      >
                        {log.status === "validated"
                          ? "Validated"
                          : "Invalidated"}
                      </span>
                    </td>
                    <td className="px-6 py-3 text-sm text-gray-600">
                      {log.distance?.toFixed(1) || 0}
                    </td>
                    <td className="px-6 py-3 text-xs text-gray-500">
                      {fmtDate(log.timestamp)}
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="5"
                    className="px-6 py-10 text-center text-gray-500 italic"
                  >
                    No attendance logs found.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        {/* ✅ Hint */}
        <p className="text-xs text-gray-400 mt-4">
          * This table updates in real-time as users check in via the mobile app.
        </p>
      </div>
    </div>
  );
}
