// src/admin/ArchivedEvents.js
import React, { useEffect, useState } from "react";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  addDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";
import Sidebar from "../components/Sidebar";
import { useNavigate } from "react-router-dom";

function ArchivedEvents() {
  const navigate = useNavigate();

  const [archivedEvents, setArchivedEvents] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [notification, setNotification] = useState(null);

  useEffect(() => {
    const unsubscribe = onSnapshot(
      collection(db, "archived_events"),
      (snapshot) => {
        const eventsData = snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setArchivedEvents(eventsData);
      }
    );

    return () => unsubscribe();
  }, []);

  // Toast
  const showNotification = (message) => {
    setNotification(message);
    setTimeout(() => setNotification(null), 3000);
  };

  const handleRestoreClick = (event) => {
    setSelectedEvent(event);
    setShowModal(true);
  };

  // Restore the FULL event object
  const confirmRestore = async () => {
    if (!selectedEvent) return;

    try {
      // Move back to "events" — keep ALL fields
      await addDoc(collection(db, "events"), {
        ...selectedEvent,
        restoredAt: new Date(),
      });

      // Remove from archived
      await deleteDoc(doc(db, "archived_events", selectedEvent.id));

      setShowModal(false);
      setSelectedEvent(null);

      showNotification("Event restored successfully!");
    } catch (error) {
      console.error("Error restoring event:", error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteDoc(doc(db, "archived_events", id));
      showNotification("Event permanently deleted.");
    } catch (error) {
      console.error("Error deleting event:", error);
    }
  };

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto relative">
        <h2 className="text-3xl font-semibold text-gray-800 mb-8">
          Archived Events
        </h2>

        {/* Notification */}
        {notification && (
          <div className="fixed top-6 right-6 bg-gray-900 text-white px-5 py-3 rounded-lg shadow-lg text-sm font-medium animate-fade-in-out z-50">
            {notification}
          </div>
        )}

        {/* TABLE */}
        <div className="bg-white shadow-xl rounded-2xl overflow-hidden border border-gray-200">
          <table className="min-w-full text-left">
            <thead className="bg-gray-800 text-gray-100">
              <tr>
                <th className="px-6 py-3 text-sm font-semibold">Title</th>
                <th className="px-6 py-3 text-sm font-semibold">Location</th>
                <th className="px-6 py-3 text-sm font-semibold">Date</th>
                <th className="px-6 py-3 text-sm font-semibold text-center">
                  Actions
                </th>
              </tr>
            </thead>

            <tbody>
              {archivedEvents.length > 0 ? (
                archivedEvents.map((event) => (
                  <tr
                    key={event.id}
                    className="border-b border-gray-200 hover:bg-gray-50 transition"
                  >
                    <td className="px-6 py-4 text-gray-800">{event.title}</td>
                    <td className="px-6 py-4 text-gray-700">
                      {event.location}
                    </td>
                    <td className="px-6 py-4 text-gray-700">{event.date}</td>

                    <td className="px-6 py-4">
                      <div className="flex justify-center gap-2">
                        <button
                          className="bg-gray-700 hover:bg-gray-900 text-white px-3 py-1 rounded-md text-sm transition font-medium"
                          onClick={() => handleRestoreClick(event)}
                        >
                          Restore
                        </button>

                        <button
                          className="bg-yellow-400 hover:bg-yellow-500 text-gray-900 px-3 py-1 rounded-md text-sm transition font-medium"
                          onClick={() => handleDelete(event.id)}
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="4"
                    className="px-6 py-8 text-center text-gray-500 bg-white"
                  >
                    No archived events available.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* RESTORE MODAL */}
      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40 backdrop-blur-sm z-50">
          <div className="bg-white rounded-xl shadow-2xl w-96 p-6 text-center">
            <h3 className="text-xl font-semibold text-gray-800 mb-4">
              Restore Event
            </h3>

            <p className="text-gray-600 mb-6">
              Are you sure you want to restore{" "}
              <span className="font-medium text-gray-900">
                “{selectedEvent?.title}”
              </span>
              ?
            </p>

            <div className="flex justify-center gap-4">
              <button
                onClick={confirmRestore}
                className="bg-gray-700 hover:bg-gray-900 text-white font-medium px-4 py-2 rounded-md transition"
              >
                Yes, Restore
              </button>

              <button
                onClick={() => setShowModal(false)}
                className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-medium px-4 py-2 rounded-md transition"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ArchivedEvents;
