// src/pages/EventFeedbacks.js
import React, { useEffect, useMemo, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  query,
  where,
  orderBy,
  addDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";
import Swal from "sweetalert2";

/** ---------- Toggle this to see sample cards when Firestore has no data ---------- */
const USE_SAMPLE_WHEN_EMPTY = true;
/** ------------------------------------------------------------------------------ */

// temporary heuristic (until VADER runs and stores fb.sentiment)
const deriveSentimentHeuristic = (fb) => {
  const rating = Number(fb?.rating);
  const text = (fb?.comment || "").toLowerCase();
  const spamWords = ["http://", "https://", "buy now", "promo", "free $$$", "visit my"];
  const isSpam = spamWords.some((w) => text.includes(w)) || fb?.isSpam === true;
  if (isSpam) return "spam";
  if (!Number.isNaN(rating)) {
    if (rating >= 4) return "positive";
    if (rating <= 2) return "negative";
  }
  return "neutral";
};

const fmtDate = (ts) => {
  try {
    const d = ts?.toDate?.() instanceof Date ? ts.toDate() : new Date(ts || Date.now());
    return d.toLocaleString();
  } catch {
    return "—";
  }
};

const Pill = ({ active, onClick, children }) => (
  <button
    onClick={onClick}
    className={
      "px-4 py-2 rounded-full text-sm transition " +
      (active ? "bg-blue-600 text-white shadow" : "bg-white text-gray-700 hover:bg-gray-100 border")
    }
  >
    {children}
  </button>
);

const FeedbackCard = ({ fb, onArchive, onDelete }) => {
  const sentiment = (fb?.sentiment || fb?._sentiment || "neutral").toLowerCase();
  const chipColor =
    sentiment === "positive"
      ? "bg-green-100 text-green-700"
      : sentiment === "negative"
      ? "bg-red-100 text-red-700"
      : sentiment === "spam"
      ? "bg-yellow-100 text-yellow-700"
      : "bg-gray-100 text-gray-700";

  return (
    <div className="bg-white rounded-xl shadow p-5 flex flex-col gap-3">
      <div className="flex items-start justify-between gap-3">
        <div>
          <div className="text-sm text-gray-500">
            {fb?.anonymous ? <em>Anonymous</em> : fb?.userName || fb?.userEmail || "User"}
          </div>
          <div className="text-xs text-gray-400">{fmtDate(fb?.createdAt)}</div>
        </div>
        <div className={`px-3 py-1 rounded-full text-xs font-medium ${chipColor} capitalize`}>
          {sentiment}
        </div>
      </div>

      <div className="flex items-center gap-2">
        <div className="text-lg font-semibold text-gray-800">{fb?.rating ?? "—"}</div>
        <div className="text-yellow-500">★</div>
      </div>

      <div className="text-gray-700">
        {fb?.comment ? fb.comment : <span className="italic text-gray-400">No comment</span>}
      </div>

      {/* Optional: show per-question answers if you store them */}
      {Array.isArray(fb?.answers) && fb.answers.length > 0 && (
        <div className="mt-1 border-t pt-3">
          <div className="text-sm font-medium text-gray-600 mb-2">Question Responses</div>
          <ul className="space-y-1">
            {fb.answers.map((a, idx) => (
              <li key={idx} className="text-sm text-gray-600">
                <span className="font-medium">{a?.question}</span>
                {": "}
                <span>{a?.answer}</span>
              </li>
            ))}
          </ul>
        </div>
      )}

      <div className="mt-2 flex gap-2">
        <button
          onClick={onArchive}
          className="px-3 py-2 text-sm rounded-md bg-yellow-500 hover:bg-yellow-600 text-white transition"
        >
          Archive
        </button>
        <button
          onClick={onDelete}
          className="px-3 py-2 text-sm rounded-md bg-red-600 hover:bg-red-700 text-white transition"
        >
          Delete
        </button>
      </div>
    </div>
  );
};

export default function EventFeedbacks() {
  const { eventId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();

  const [eventName] = useState(location?.state?.eventName || "Event");
  const [feedbacks, setFeedbacks] = useState([]);
  const [active, setActive] = useState(
    new URLSearchParams(location.search).get("sentiment") || "all"
  );

  // Firestore: live feedbacks for this event
  useEffect(() => {
    if (!eventId) return;
    const qRef = query(
      collection(db, "feedbacks"),
      where("eventId", "==", eventId),
      orderBy("createdAt", "desc")
    );
    const unsub = onSnapshot(qRef, (snap) => {
      const list = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
      setFeedbacks(list);
    });
    return () => unsub();
  }, [eventId]);

  // Optional sample cards while you don't have data yet
  const withSample = useMemo(() => {
    if (feedbacks.length > 0 || !USE_SAMPLE_WHEN_EMPTY) return feedbacks;
    return [
      {
        id: "sample1",
        userName: "Jane Student",
        rating: 5,
        comment: "Loved the event! Speakers were amazing.",
        sentiment: "positive",
        createdAt: new Date(),
        answers: [{ question: "Was the venue comfortable?", answer: "Yes" }],
      },
      {
        id: "sample2",
        userEmail: "user@domain.com",
        rating: 2,
        comment: "Audio issues, couldn't hear from the back.",
        sentiment: "negative",
        createdAt: new Date(),
      },
      {
        id: "sample3",
        anonymous: true,
        rating: 1,
        comment: "Visit my site http://spam.example.com for free gifts",
        sentiment: "spam",
        createdAt: new Date(),
      },
    ];
  }, [feedbacks]);

  // add derived sentiment if not present
  const enriched = useMemo(
    () =>
      withSample.map((fb) => ({
        ...fb,
        _sentiment: fb?.sentiment || deriveSentimentHeuristic(fb),
      })),
    [withSample]
  );

  // Stats
  const stats = useMemo(() => {
    const ratings = enriched.map((f) => Number(f.rating)).filter((n) => !Number.isNaN(n));
    const total = enriched.length;
    const avg = ratings.length
      ? Number((ratings.reduce((a, b) => a + b, 0) / ratings.length).toFixed(2))
      : 0;
    const positive = enriched.filter((f) => f._sentiment === "positive").length;
    const negative = enriched.filter((f) => f._sentiment === "negative").length;
    const spam = enriched.filter((f) => f._sentiment === "spam").length;
    return { total, avg, positive, negative, spam };
  }, [enriched]);

  // Filter by active sentiment
  const visible = useMemo(() => {
    if (active === "all") return enriched;
    return enriched.filter((f) => f._sentiment === active);
  }, [active, enriched]);

  // sync active pill to URL ?sentiment=
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    if (active === "all") params.delete("sentiment");
    else params.set("sentiment", active);
    navigate({ search: params.toString() }, { replace: true });
  }, [active]); // eslint-disable-line

  // Actions
  const handleArchive = async (id, fb) => {
    const confirm = await Swal.fire({
      title: "Archive this feedback?",
      text: "It will move to Archived Feedbacks.",
      icon: "question",
      showCancelButton: true,
      confirmButtonColor: "#1abc9c",
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Yes, archive",
    });
    if (!confirm.isConfirmed) return;
    try {
      await addDoc(collection(db, "archived_feedbacks"), {
        ...fb,
        archivedAt: new Date(),
      });
      await deleteDoc(doc(db, "feedbacks", id));
      Swal.fire({ icon: "success", title: "Archived", timer: 1400, showConfirmButton: false });
    } catch (e) {
      console.error(e);
      Swal.fire({ icon: "error", title: "Failed to archive", text: e?.message || "" });
    }
  };

  const handleDelete = async (id) => {
    const confirm = await Swal.fire({
      title: "Delete this feedback?",
      text: "This cannot be undone.",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#e74c3c",
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Yes, delete",
    });
    if (!confirm.isConfirmed) return;
    try {
      await deleteDoc(doc(db, "feedbacks", id));
      Swal.fire({ icon: "success", title: "Deleted", timer: 1300, showConfirmButton: false });
    } catch (e) {
      console.error(e);
      Swal.fire({ icon: "error", title: "Failed to delete", text: e?.message || "" });
    }
  };

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">
        {/* Header */}
        <h1 className="text-3xl font-semibold text-gray-800 mb-1">
          Feedbacks — <span className="text-blue-600">{eventName}</span>
        </h1>

        {/* Single stats line (no containers) */}
        <p className="text-gray-500 mb-6">
          Total: <span className="font-semibold text-gray-700">{stats.total}</span>
          {" • "}
          Average Rating:{" "}
          <span className="font-semibold text-gray-700">
            {stats.avg} <span className="text-yellow-500">★</span>
          </span>
          {" • "}
          Positive: <span className="font-semibold text-gray-700">{stats.positive}</span>
          {" • "}
          Negative: <span className="font-semibold text-gray-700">{stats.negative}</span>
          {" • "}
          Spam: <span className="font-semibold text-gray-700">{stats.spam}</span>
        </p>

        {/* Pills */}
        <div className="flex flex-wrap gap-3 mb-6">
          <Pill active={active === "all"} onClick={() => setActive("all")}>
            All <span className="ml-2 bg-white/20 rounded-full px-2">{stats.total}</span>
          </Pill>
          <Pill active={active === "positive"} onClick={() => setActive("positive")}>
            Positive <span className="ml-2 bg-white/20 rounded-full px-2">{stats.positive}</span>
          </Pill>
          <Pill active={active === "negative"} onClick={() => setActive("negative")}>
            Negative <span className="ml-2 bg-white/20 rounded-full px-2">{stats.negative}</span>
          </Pill>
          <Pill active={active === "spam"} onClick={() => setActive("spam")}>
            Spam <span className="ml-2 bg-white/20 rounded-full px-2">{stats.spam}</span>
          </Pill>
        </div>

        {/* Cards grid */}
        {visible.length === 0 ? (
          <div className="bg-white rounded-xl shadow p-8 text-center text-gray-500">
            No feedbacks in this category.
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {visible.map((fb) => (
              <FeedbackCard
                key={fb.id}
                fb={fb}
                onArchive={() => handleArchive(fb.id, fb)}
                onDelete={() => handleDelete(fb.id)}
              />
            ))}
          </div>
        )}

        {/* Tip for VADER integration */}
        <p className="text-xs text-gray-400 mt-6">
        NOTE: No VADER integrated yet, test data examples.
        </p>
      </div>
    </div>
  );
}
