// src/pages/AddNotifications.js
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  query,
  where,
  limit,
  doc,
  getDoc,
  updateDoc,
  arrayUnion,
} from "firebase/firestore";
import Swal from "sweetalert2";

export default function AddNotifications() {
  const navigate = useNavigate();

  const [events, setEvents] = useState([]);
  const [selectedId, setSelectedId] = useState("");
  const [selectedDoc, setSelectedDoc] = useState(null);

  const [title, setTitle] = useState("");
  const [body, setBody] = useState("");

  // Load all events (live)
  useEffect(() => {
    const unsub = onSnapshot(collection(db, "events"), (snap) => {
      const list = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
      // Try to keep current event on top
      list.sort((a, b) => (b.isCurrent === true) - (a.isCurrent === true));
      setEvents(list);
    });
    return () => unsub();
  }, []);

  // Pick default: current event if present
  useEffect(() => {
    if (!events.length) return;
    if (!selectedId) {
      const current = events.find((e) => e.isCurrent);
      setSelectedId(current ? current.id : events[0].id);
    }
  }, [events, selectedId]);

  // Live watch selected event doc
  useEffect(() => {
    if (!selectedId) return;
    const ref = doc(db, "events", selectedId);
    const unsub = onSnapshot(ref, (d) => setSelectedDoc({ id: d.id, ...d.data() }));
    return () => unsub();
  }, [selectedId]);

  const notifications = useMemo(() => {
    const arr = Array.isArray(selectedDoc?.notifications) ? selectedDoc.notifications : [];
    // Sort newest first if createdAt exists
    return [...arr].sort((a, b) => {
      const ta = (a?.createdAt?.seconds ?? 0);
      const tb = (b?.createdAt?.seconds ?? 0);
      return tb - ta;
    });
  }, [selectedDoc]);

  const handleAdd = async (e) => {
    e.preventDefault();
    if (!selectedId) {
      Swal.fire("No Event", "Select an event first.", "warning");
      return;
    }
    if (!title.trim() && !body.trim()) {
      Swal.fire("Missing content", "Add a title or a body.", "warning");
      return;
    }
    try {
      const ref = doc(db, "events", selectedId);
      await updateDoc(ref, {
        notifications: arrayUnion({
          title: title.trim() || undefined,
          body: body.trim() || "",
          createdAt: new Date(),
        }),
      });
      setTitle("");
      setBody("");
      Swal.fire({ icon: "success", title: "Added", timer: 1200, showConfirmButton: false });
    } catch (err) {
      console.error(err);
      Swal.fire("Error", "Failed to add notification.", "error");
    }
  };

  // Remove one notification by index -> write full new array
  const handleDeleteOne = async (idx) => {
    if (!selectedId) return;
    const ok = await Swal.fire({
      title: "Delete this notification?",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#e74c3c",
      confirmButtonText: "Delete",
    });
    if (!ok.isConfirmed) return;

    try {
      const ref = doc(db, "events", selectedId);
      const snap = await getDoc(ref);
      const arr = Array.isArray(snap.data()?.notifications) ? snap.data().notifications : [];
      const next = arr.filter((_, i) => i !== idx);
      await updateDoc(ref, { notifications: next });
      Swal.fire({ icon: "success", title: "Deleted", timer: 1000, showConfirmButton: false });
    } catch (err) {
      console.error(err);
      Swal.fire("Error", "Failed to delete notification.", "error");
    }
  };

  // Clear all notifications
  const handleClearAll = async () => {
    if (!selectedId) return;
    const ok = await Swal.fire({
      title: "Clear ALL notifications?",
      text: "This cannot be undone.",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#e74c3c",
      confirmButtonText: "Clear all",
    });
    if (!ok.isConfirmed) return;

    try {
      await updateDoc(doc(db, "events", selectedId), { notifications: [] });
      Swal.fire({ icon: "success", title: "Cleared", timer: 1000, showConfirmButton: false });
    } catch (err) {
      console.error(err);
      Swal.fire("Error", "Failed to clear notifications.", "error");
    }
  };

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">
        <div className="flex items-start justify-between mb-6">
          <h1 className="text-3xl font-semibold text-gray-800">Add Notifications</h1>
          <button
            onClick={() => navigate("/admin/manage-events")}
            className="px-4 py-2 rounded-md border bg-white hover:bg-gray-100 text-gray-700"
          >
            Manage Events
          </button>
        </div>

        {/* Event picker */}
        <div className="bg-white rounded-xl shadow p-6 mb-6">
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Select Event {selectedDoc?.isCurrent && <span className="ml-2 text-xs text-emerald-600 font-semibold">CURRENT</span>}
          </label>
          <select
            className="w-full border rounded-md px-3 py-2"
            value={selectedId || ""}
            onChange={(e) => setSelectedId(e.target.value)}
          >
            <option value="" disabled>
              -- Select an event --
            </option>
            {events.map((ev) => (
              <option key={ev.id} value={ev.id}>
                {ev.title || "Untitled"} {ev.isCurrent ? " â€¢ (Current)" : ""}
              </option>
            ))}
          </select>
          {selectedDoc?.location && (
            <p className="mt-2 text-sm text-gray-500">Location: {selectedDoc.location}</p>
          )}
          {selectedDoc?.date && (
            <p className="text-sm text-gray-500">Date: {selectedDoc.date}</p>
          )}
        </div>

        {/* Add form */}
        <form onSubmit={handleAdd} className="bg-white rounded-xl shadow p-6 mb-6">
          <h2 className="text-lg font-semibold text-gray-800 mb-4">Create Notification</h2>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <input
              type="text"
              placeholder="Subject (optional)"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              className="border rounded-md px-3 py-2 w-full"
            />
            <input
              type="text"
              placeholder="Message"
              value={body}
              onChange={(e) => setBody(e.target.value)}
              className="border rounded-md px-3 py-2 w-full"
              required
            />
          </div>

          <div className="mt-4 flex items-center gap-3">
            <button
              type="submit"
              className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md"
              disabled={!selectedId}
              title={!selectedId ? "Select an event first" : ""}
            >
              Add Notification
            </button>
            <button
              type="button"
              onClick={handleClearAll}
              className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-md"
              disabled={!selectedId || notifications.length === 0}
            >
              Clear All
            </button>
          </div>
        </form>

        {/* Existing notifications */}
        <div className="bg-white rounded-xl shadow p-6">
          <h2 className="text-lg font-semibold text-gray-800 mb-4">Existing Notifications</h2>

          {!selectedId ? (
            <p className="text-gray-500">Select an event to view notifications.</p>
          ) : notifications.length === 0 ? (
            <p className="text-gray-500">No notifications for this event.</p>
          ) : (
            <ul className="space-y-3">
              {notifications.map((n, idx) => (
                <li
                  key={idx}
                  className="border rounded-lg p-4 flex items-start justify-between gap-4"
                >
                  <div>
                    {n.title && (
                      <p className="font-semibold text-gray-800">
                        Subject: <span className="font-bold">{n.title}</span>
                      </p>
                    )}
                    <p className="text-gray-700">{n.body || ""}</p>
                  </div>
                  <button
                    onClick={() => handleDeleteOne(idx)}
                    className="bg-red-600 hover:bg-red-700 text-white px-3 py-1.5 rounded-md text-sm"
                  >
                    Delete
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>

        <p className="text-xs text-gray-400 mt-6">
          Note: Mobile reads notifications from the <code>notifications</code> array on the event.
          Mark an event as <strong>Current</strong> in Manage Events to make it appear on usersâ€™ phones.
        </p>
      </div>
    </div>
  );
}
