// src/pages/EditQuestions.js
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Sidebar from "../components/Sidebar";
import { db } from "../firebase/firebaseConfig";
import {
  doc,
  getDoc,
  setDoc,
  updateDoc,
  arrayUnion,
  arrayRemove,
  serverTimestamp,
} from "firebase/firestore";
import Swal from "sweetalert2";

const COLLECTION = "event_questions"; // one doc per eventId

function EditQuestions() {
  const { eventId } = useParams();
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [newQuestion, setNewQuestion] = useState("");
  const [loading, setLoading] = useState(true);

  // Load questions for this event from event_questions/{eventId}
  useEffect(() => {
    if (!eventId) return;
    (async () => {
      try {
        const ref = doc(db, COLLECTION, eventId);
        const snap = await getDoc(ref);

        if (!snap.exists()) {
          // Create an empty doc if missing (no defaults, real data only)
          await setDoc(ref, {
            eventId,
            questions: [],
            updatedAt: serverTimestamp(),
          });
          setQuestions([]);
        } else {
          const data = snap.data();
          setQuestions(Array.isArray(data?.questions) ? data.questions : []);
        }
      } catch (e) {
        console.error(e);
        Swal.fire("Error", "Failed to fetch questions", "error");
      } finally {
        setLoading(false);
      }
    })();
  }, [eventId]);

  // Add a new question (push into questions[])
  const handleAdd = async (e) => {
    e.preventDefault();
    const q = newQuestion.trim();
    if (!q) return;

    try {
      const ref = doc(db, COLLECTION, eventId);
      await updateDoc(ref, {
        questions: arrayUnion(q),
        updatedAt: serverTimestamp(),
      });
      setQuestions((prev) => [...prev, q]);
      setNewQuestion("");
      Swal.fire({
        icon: "success",
        title: "Added",
        text: "New question added successfully",
        timer: 1500,
        showConfirmButton: false,
      });
    } catch (err) {
      console.error(err);
      Swal.fire("Error", "Failed to add question", "error");
    }
  };

  // Remove a question (remove from questions[])
  const handleDelete = async (q) => {
    const confirm = await Swal.fire({
      title: "Delete this question?",
      text: "This cannot be undone",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#e74c3c",
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Yes, delete it",
    });
    if (!confirm.isConfirmed) return;

    try {
      const ref = doc(db, COLLECTION, eventId);
      await updateDoc(ref, {
        questions: arrayRemove(q),
        updatedAt: serverTimestamp(),
      });
      setQuestions((prev) => prev.filter((x) => x !== q));
      Swal.fire({
        icon: "success",
        title: "Deleted",
        timer: 1200,
        showConfirmButton: false,
      });
    } catch (err) {
      console.error(err);
      Swal.fire("Error", "Failed to delete question", "error");
    }
  };

  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      {/* Sidebar */}
      <Sidebar />

      {/* Main */}
      <div className="flex-1 p-10 overflow-y-auto">
        {/* Header */}
        <div className="flex items-start justify-between mb-6">
          <h1 className="text-3xl font-semibold text-gray-800">Edit Questions</h1>
          <button
            onClick={() => navigate(-1)}
            className="px-4 py-2 rounded-full text-sm border bg-white hover:bg-gray-100 text-gray-700 shadow-sm"
          >
            Back
          </button>
        </div>

        {/* Add New Question */}
        <form
          onSubmit={handleAdd}
          className="bg-white shadow-md rounded-xl p-6 mb-8 flex gap-4"
        >
          <input
            type="text"
            placeholder="Enter a new question..."
            value={newQuestion}
            onChange={(e) => setNewQuestion(e.target.value)}
            className="flex-1 px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-500"
          />
          <button
            type="submit"
            className="bg-blue-600 hover:bg-blue-700 text-white px-5 py-2 rounded-md transition"
          >
            Add
          </button>
        </form>

        {/* Questions List */}
        <div className="bg-white shadow-lg rounded-xl overflow-hidden">
          <table className="min-w-full text-left text-gray-700">
            <thead className="bg-blue-600 text-white">
              <tr>
                <th className="px-6 py-3 text-sm font-medium w-12">#</th>
                <th className="px-6 py-3 text-sm font-medium">Question</th>
                <th className="px-6 py-3 text-sm font-medium text-center">Actions</th>
              </tr>
            </thead>
            <tbody>
              {loading ? (
                <tr>
                  <td colSpan="3" className="px-6 py-10 text-center text-gray-500">
                    Loading…
                  </td>
                </tr>
              ) : questions.length ? (
                questions.map((q, i) => (
                  <tr key={q} className="border-b hover:bg-gray-50 transition">
                    <td className="px-6 py-4">{i + 1}</td>
                    <td className="px-6 py-4">{q}</td>
                    <td className="px-6 py-4 text-center">
                      <button
                        onClick={() => handleDelete(q)}
                        className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded-md text-sm"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="3" className="px-6 py-10 text-center text-gray-500">
                    No questions yet. Add one above!
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>

        <p className="text-xs text-gray-400 mt-4">
          Stored at <code>{COLLECTION}/{eventId}</code> with a single <code>questions[]</code> array.
        </p>
      </div>
    </div>
  );
}

export default EditQuestions;
