import React from "react";
import { useNavigate } from "react-router-dom";

// Import logos/icons
import logo from "../assets/images/logo.png";
import dashboardIcon from "../assets/images/dashboard.png";
import manageEventsIcon from "../assets/images/manage_events.png";
import archiveIcon from "../assets/images/archive.png";
import addEventsIcon from "../assets/images/add_events.png";

function Dashboard() {
  const navigate = useNavigate();

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <div className="w-64 bg-[#1c2239] text-white flex flex-col">
        {/* Logo */}
        <div className="flex flex-col items-center py-6 border-b border-gray-700">
          <img src={logo} alt="Logo" className="w-16 h-16 mb-2" />
          <h2 className="text-xl font-bold tracking-wide">ADVENT</h2>
        </div>

        {/* Menu */}
        <ul className="flex-1 mt-6 space-y-2 px-4">
          <li
            className="flex items-center gap-3 p-2 rounded-md hover:bg-gray-700 cursor-pointer"
            onClick={() => navigate("/admin/dashboard")}
          >
            <img src={dashboardIcon} alt="Dashboard" className="w-5 h-5" />
            <span>Dashboard</span>
          </li>
          <li
            className="flex items-center gap-3 p-2 rounded-md hover:bg-gray-700 cursor-pointer"
            onClick={() => navigate("/admin/manage-events")}
          >
            <img src={manageEventsIcon} alt="Manage Events" className="w-5 h-5" />
            <span>Manage Events</span>
          </li>
          <li
            className="flex items-center gap-3 p-2 rounded-md hover:bg-gray-700 cursor-pointer"
            onClick={() => navigate("/admin/archived-events")}
          >
            <img src={archiveIcon} alt="Archived Events" className="w-5 h-5" />
            <span>Archived Events</span>
          </li>
          <li
            className="flex items-center gap-3 p-2 rounded-md hover:bg-gray-700 cursor-pointer"
            onClick={() => navigate("/admin/add-event")}
          >
            <img src={addEventsIcon} alt="Add Event" className="w-5 h-5" />
            <span>Add Event</span>
          </li>
        </ul>

        {/* Logout */}
        <div className="p-4 border-t border-gray-700">
          <button
            className="w-full bg-red-500 hover:bg-red-600 text-white py-2 rounded-md font-bold"
            onClick={() => navigate("/login")}
          >
            Logout
          </button>
        </div>
      </div>

      {/* Main content placeholder */}
      <div className="flex-1 p-6 bg-gray-100">
        <h1 className="text-2xl font-bold">Dashboard Content Here</h1>
      </div>
    </div>
  );
}

export default Dashboard;
