// src/pages/ArchivedEvents.js
import React, { useEffect, useState } from "react";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  addDoc,
  deleteDoc,
  doc,
  getDoc,
  setDoc,
  getDocs,
  query,
  where,
} from "firebase/firestore";
import Sidebar from "../components/Sidebar";
import Swal from "sweetalert2";

function ArchivedEvents() {
  const [archivedEvents, setArchivedEvents] = useState([]);
  const [showRestoreModal, setShowRestoreModal] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);

  const [showFeedbacksModal, setShowFeedbacksModal] = useState(false);
  const [archivedFeedbacks, setArchivedFeedbacks] = useState([]);
  const [feedbackLoading, setFeedbackLoading] = useState(false);

  const [detailOpen, setDetailOpen] = useState(false);
  const [selectedFeedback, setSelectedFeedback] = useState(null);

  /* ---------------- LOAD ARCHIVED EVENTS ---------------- */
  useEffect(() => {
    const unsub = onSnapshot(collection(db, "archived_events"), (snap) => {
      const list = snap.docs.map((d) => ({ id: d.id, ...d.data() }));
      setArchivedEvents(list);
    });

    return () => unsub();
  }, []);

  const openRestoreModal = (event) => {
    setSelectedEvent(event);
    setShowRestoreModal(true);
  };

  /* ---------------- FULL RESTORE EVENT + QUESTIONS + FEEDBACKS ---------------- */
  const handleRestore = async () => {
    if (!selectedEvent) return;

    try {
      const eventId = selectedEvent.originalId || selectedEvent.id;

      // Restore event document fully
      const restoreEvent = {
        ...(selectedEvent || {}),
        archivedAt: null,
        restoredAt: new Date(),
      };

      await setDoc(doc(db, "events", eventId), restoreEvent);

      // Restore event_questions if stored
      if (selectedEvent.event_questions) {
        await setDoc(doc(db, "event_questions", eventId), {
          questions: selectedEvent.event_questions.questions || [],
          openEnded: selectedEvent.event_questions.openEnded || [],
          createdAt: selectedEvent.event_questions.createdAt || new Date(),
          updatedAt: new Date(),
        });
      }

      /* Restore feedbacks from archived_event_feedbacks */
      const q1 = query(
        collection(db, "archived_event_feedbacks"),
        where("eventId", "==", eventId)
      );
      const snap1 = await getDocs(q1);
      for (const fb of snap1.docs) {
        await addDoc(collection(db, "feedbacks"), {
          ...fb.data(),
          restoredAt: new Date(),
        });
        await deleteDoc(doc(db, "archived_event_feedbacks", fb.id));
      }

      /* Restore feedbacks from archived_feedbacks */
      const q2 = query(
        collection(db, "archived_feedbacks"),
        where("eventId", "==", eventId)
      );
      const snap2 = await getDocs(q2);
      for (const fb of snap2.docs) {
        await addDoc(collection(db, "feedbacks"), {
          ...fb.data(),
          restoredAt: new Date(),
        });
        await deleteDoc(doc(db, "archived_feedbacks", fb.id));
      }

      // Remove from archive
      await deleteDoc(doc(db, "archived_events", selectedEvent.id));

      Swal.fire({
        icon: "success",
        title: "Event Restored",
        text: "Event, questions, and feedbacks restored successfully!",
      });

      setShowRestoreModal(false);
      setSelectedEvent(null);
    } catch (err) {
      console.error("RESTORE FAILED:", err);
      Swal.fire("Error", "Failed to restore event", "error");
    }
  };

  /* ---------------- DELETE ARCHIVED EVENT ---------------- */
  const handleDeleteArchive = async (id) => {
    const confirm = await Swal.fire({
      title: "Delete Archived Event?",
      text: "This cannot be undone.",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#d33",
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Delete",
    });

    if (!confirm.isConfirmed) return;

    try {
      await deleteDoc(doc(db, "archived_events", id));
      Swal.fire("Deleted", "Archived event removed", "success");
    } catch (err) {
      console.error("Delete failed:", err);
      Swal.fire("Error", "Failed to delete archived event", "error");
    }
  };

  /* ---------------- VIEW ARCHIVED FEEDBACKS ---------------- */
  const openFeedbacks = async (event) => {
    setSelectedEvent(event);
    setFeedbackLoading(true);
    setArchivedFeedbacks([]);

    try {
      const eventId = event.originalId || event.id;
      const results = [];

      const q1 = query(
        collection(db, "archived_event_feedbacks"),
        where("eventId", "==", eventId)
      );
      const snap1 = await getDocs(q1);
      snap1.forEach((d) => results.push({ id: d.id, ...d.data() }));

      const q2 = query(
        collection(db, "archived_feedbacks"),
        where("eventId", "==", eventId)
      );
      const snap2 = await getDocs(q2);
      snap2.forEach((d) => results.push({ id: d.id, ...d.data() }));

      results.sort((a, b) => {
        const ta = a.archivedAt?.toMillis?.() ?? a.createdAt?.toMillis?.() ?? 0;
        const tb = b.archivedAt?.toMillis?.() ?? b.createdAt?.toMillis?.() ?? 0;
        return tb - ta;
      });

      setArchivedFeedbacks(results);
      setShowFeedbacksModal(true);
    } catch (err) {
      console.error(err);
      Swal.fire("Error", "Failed to load archived feedbacks", "error");
    } finally {
      setFeedbackLoading(false);
    }
  };

  /* ---------------- OPEN FEEDBACK DETAIL ---------------- */
  const openFeedbackDetail = (fb) => {
    setSelectedFeedback(fb);
    setDetailOpen(true);
  };

  /* ----------------------------------- UI ----------------------------------- */
  return (
    <div className="flex h-screen bg-gray-100 font-poppins">
      <Sidebar />

      <div className="flex-1 p-10 overflow-y-auto">
        <h2 className="text-3xl font-semibold text-gray-800 mb-8">
          Archived Events
        </h2>

        <div className="bg-white shadow-xl rounded-2xl overflow-hidden border border-gray-200">
          <table className="min-w-full text-left">
            <thead className="bg-gray-800 text-gray-100">
              <tr>
                <th className="px-6 py-3">Title</th>
                <th className="px-6 py-3">Location</th>
                <th className="px-6 py-3">Date</th>
                <th className="px-6 py-3 text-center">Actions</th>
              </tr>
            </thead>

            <tbody>
              {archivedEvents.length > 0 ? (
                archivedEvents.map((ev) => (
                  <tr key={ev.id} className="border-b hover:bg-gray-50">
                    <td className="px-6 py-4">{ev.title}</td>
                    <td className="px-6 py-4">{ev.location}</td>
                    <td className="px-6 py-4">{ev.date}</td>
                    <td className="px-6 py-4">
                      <div className="flex justify-center gap-2">
                        <button
                          onClick={() => openRestoreModal(ev)}
                          className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded-md text-sm"
                        >
                          Restore
                        </button>
                        <button
                          onClick={() => openFeedbacks(ev)}
                          className="bg-gray-700 hover:bg-gray-900 text-white px-3 py-1 rounded-md text-sm"
                        >
                          View Feedbacks
                        </button>
                        <button
                          onClick={() => handleDeleteArchive(ev.id)}
                          className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-md text-sm"
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td
                    colSpan="4"
                    className="text-center py-6 text-gray-500 bg-white"
                  >
                    No archived events available.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Restore Modal */}
      {showRestoreModal && selectedEvent && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded-xl shadow-xl w-96">
            <h3 className="text-xl font-semibold mb-4">Restore Event</h3>
            <p className="text-gray-600 mb-6">
              Restore{" "}
              <span className="font-bold">“{selectedEvent.title}”</span> back to
              active events?
            </p>

            <div className="flex justify-center gap-4">
              <button
                onClick={handleRestore}
                className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-md"
              >
                Restore
              </button>
              <button
                onClick={() => setShowRestoreModal(false)}
                className="bg-gray-300 hover:bg-gray-400 px-4 py-2 rounded-md"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Feedback List Modal */}
      {showFeedbacksModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-start justify-center pt-16 z-50 p-4">
          <div className="bg-white rounded-xl shadow-xl w-full max-w-4xl max-h-[80vh] overflow-auto">
            <div className="flex justify-between items-center p-4 border-b">
              <h3 className="text-lg font-semibold">
                Archived Feedbacks — {selectedEvent?.title}
              </h3>
              <button
                onClick={() => {
                  setShowFeedbacksModal(false);
                  setArchivedFeedbacks([]);
                  setSelectedEvent(null);
                }}
                className="text-gray-600 hover:text-gray-800"
              >
                ✕
              </button>
            </div>

            <div className="p-4">
              {feedbackLoading ? (
                <div className="text-center py-6">Loading feedbacks...</div>
              ) : archivedFeedbacks.length === 0 ? (
                <div className="text-center py-6 text-gray-500">
                  No archived feedbacks.
                </div>
              ) : (
                <div className="overflow-x-auto">
                  <table className="min-w-full text-left">
                    <thead>
                      <tr className="bg-gray-50">
                        <th className="px-4 py-2">Student ID</th>
                        <th className="px-4 py-2">Name</th>
                        <th className="px-4 py-2">Comment</th>
                        <th className="px-4 py-2 text-center">Sentiment</th>
                        <th className="px-4 py-2 text-center">Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {archivedFeedbacks.map((fb) => (
                        <tr key={fb.id} className="border-t hover:bg-gray-50">
                          <td className="px-4 py-2">
                            {fb.studentID ?? fb.userId ?? "—"}
                          </td>
                          <td className="px-4 py-2">
                            {fb.anonymous
                              ? "Anonymous"
                              : fb.userName ||
                                fb.displayName ||
                                fb.userEmail ||
                                "User"}
                          </td>
                          <td className="px-4 py-2 truncate max-w-[30ch]">
                            {fb.comment}
                          </td>
                          <td className="px-4 py-2 text-center">
                            {fb.sentiment ?? "neutral"}
                          </td>
                          <td className="px-4 py-2 text-center">
                            <button
                              onClick={() => openFeedbackDetail(fb)}
                              className="bg-gray-700 hover:bg-gray-900 text-white px-2 py-1 rounded-md text-xs"
                            >
                              View
                            </button>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Feedback Detail Modal */}
      {detailOpen && selectedFeedback && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-xl shadow-xl w-full max-w-3xl max-h-[85vh] overflow-auto">
            <div className="flex justify-between items-center p-4 border-b">
              <h3 className="text-lg font-semibold">Feedback Detail</h3>
              <button
                onClick={() => {
                  setDetailOpen(false);
                  setSelectedFeedback(null);
                }}
                className="text-gray-600 hover:text-gray-800"
              >
                ✕
              </button>
            </div>

            <div className="p-6 space-y-4">
              <div>
                <label className="text-sm text-gray-500">Comment</label>
                <div className="p-3 border rounded bg-gray-50">
                  {selectedFeedback.comment}
                </div>
              </div>

              <div>
                <label className="text-sm text-gray-500">Ratings</label>
                <div className="p-3 border rounded bg-gray-50">
                  {(selectedFeedback.ratings
                    ? Array.isArray(selectedFeedback.ratings)
                      ? selectedFeedback.ratings
                      : Object.values(selectedFeedback.ratings)
                    : []
                  ).map((r, i) => (
                    <div key={i} className="text-sm">
                      Q{i + 1}: {r}
                    </div>
                  ))}
                </div>
              </div>

              <div>
                <label className="text-sm text-gray-500">
                  Open-ended Answers
                </label>
                {(selectedFeedback.answers
                  ? Array.isArray(selectedFeedback.answers)
                    ? selectedFeedback.answers
                    : Object.values(selectedFeedback.answers)
                  : []
                ).length > 0 ? (
                  <ul className="list-disc pl-6 space-y-1 text-sm">
                    {(selectedFeedback.answers
                      ? Array.isArray(selectedFeedback.answers)
                        ? selectedFeedback.answers
                        : Object.values(selectedFeedback.answers)
                      : []
                    ).map((a, i) => (
                      <li key={i}>{a}</li>
                    ))}
                  </ul>
                ) : (
                  <div className="text-gray-500 text-sm italic">
                    No open-ended responses
                  </div>
                )}
              </div>

              <button
                onClick={() => {
                  setDetailOpen(false);
                  setSelectedFeedback(null);
                }}
                className="px-4 py-2 border rounded bg-white hover:bg-gray-100"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default ArchivedEvents;
