import React, { useEffect, useState } from "react";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  onSnapshot,
  updateDoc,
  doc,
} from "firebase/firestore";
import Swal from "sweetalert2";
import Sidebar from "../components/Sidebar";

function ManageEvents() {
  const [events, setEvents] = useState([]);
  const [archivedEvents, setArchivedEvents] = useState([]);

  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, "events"), (snapshot) => {
      const allEvents = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setEvents(allEvents.filter((event) => !event.isArchived));
      setArchivedEvents(allEvents.filter((event) => event.isArchived));
    });

    return () => unsubscribe();
  }, []);

  const handleSetCurrent = async (id) => {
    try {
      // Remove current from all events
      const promises = events.map((event) =>
        updateDoc(doc(db, "events", event.id), { isCurrent: event.id === id })
      );
      await Promise.all(promises);

      Swal.fire({
        icon: "success",
        title: "Current event updated!",
        timer: 2000,
        showConfirmButton: false,
      });
    } catch (error) {
      console.error("Error setting current event:", error);
      Swal.fire({
        icon: "error",
        title: "Failed to update event",
        timer: 2000,
        showConfirmButton: false,
      });
    }
  };

  const handleArchive = async (id) => {
    const confirm = await Swal.fire({
      title: "Archive this event?",
      text: "This event will be moved to Archived Events.",
      icon: "question",
      showCancelButton: true,
      confirmButtonColor: "#e74c3c", // red
      cancelButtonColor: "#3085d6",
      confirmButtonText: "Yes, archive it!",
    });

    if (confirm.isConfirmed) {
      try {
        await updateDoc(doc(db, "events", id), { isArchived: true });
        Swal.fire({
          icon: "success",
          title: "Archived!",
          text: "Event moved to Archived Events.",
          timer: 2000,
          showConfirmButton: false,
        });
      } catch (error) {
        console.error("Error archiving event:", error);
        Swal.fire({
          icon: "error",
          title: "Failed to archive event",
          timer: 2000,
          showConfirmButton: false,
        });
      }
    }
  };

  const handleUnarchive = async (id) => {
    try {
      await updateDoc(doc(db, "events", id), { isArchived: false });
      Swal.fire({
        icon: "success",
        title: "Event Restored!",
        text: "Event has been moved back to Active Events.",
        timer: 2000,
        showConfirmButton: false,
      });
    } catch (error) {
      console.error("Error unarchiving event:", error);
      Swal.fire({
        icon: "error",
        title: "Failed to restore event",
        timer: 2000,
        showConfirmButton: false,
      });
    }
  };

  return (
    <div className="flex h-screen bg-gray-50 font-poppins">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 p-10 overflow-y-auto">
        <h2 className="text-3xl font-semibold text-gray-800 mb-8">
          Manage Events
        </h2>

        {/* Active Events Table */}
        <div className="mb-12">
          <h3 className="text-xl font-semibold text-gray-700 mb-4">
            Active Events
          </h3>
          <div className="bg-[#1E293B] shadow-lg rounded-xl overflow-hidden">
            <table className="min-w-full text-left text-gray-800">
              <thead className="bg-[#0F172A] text-white">
                <tr>
                  <th className="px-6 py-3 text-sm font-medium">Title</th>
                  <th className="px-6 py-3 text-sm font-medium">Location</th>
                  <th className="px-6 py-3 text-sm font-medium">Date</th>
                  <th className="px-6 py-3 text-sm font-medium">Current</th>
                  <th className="px-6 py-3 text-sm font-medium text-center">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody>
                {events.length > 0 ? (
                  events.map((event) => (
                    <tr
                      key={event.id}
                      className="border-b border-gray-300 hover:bg-gray-100 transition bg-white"
                    >
                      <td className="px-6 py-4">{event.title}</td>
                      <td className="px-6 py-4">{event.location}</td>
                      <td className="px-6 py-4">{event.date}</td>
                      <td className="px-6 py-4">
                        {event.isCurrent ? (
                          <span className="px-2 py-1 text-xs rounded-full bg-yellow-200 text-yellow-900 font-semibold">
                            CURRENT
                          </span>
                        ) : (
                          <button
                            className="bg-yellow-500 hover:bg-yellow-600 text-white px-3 py-1 rounded-md text-sm"
                            onClick={() => handleSetCurrent(event.id)}
                          >
                            Set Current
                          </button>
                        )}
                      </td>
                      <td className="px-6 py-4 flex gap-2 justify-center">
                        <button
                          className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded-md text-sm"
                          onClick={() => handleArchive(event.id)}
                        >
                          Archive
                        </button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td
                      colSpan="5"
                      className="px-6 py-8 text-center text-gray-500 bg-white"
                    >
                      No active events available.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>

        {/* Archived Events Table */}
        <div>
          <h3 className="text-xl font-semibold text-gray-700 mb-4">
            Archived Events
          </h3>
          <div className="bg-[#1E293B] shadow-lg rounded-xl overflow-hidden">
            <table className="min-w-full text-left text-gray-800">
              <thead className="bg-[#0F172A] text-white">
                <tr>
                  <th className="px-6 py-3 text-sm font-medium">Title</th>
                  <th className="px-6 py-3 text-sm font-medium">Location</th>
                  <th className="px-6 py-3 text-sm font-medium">Date</th>
                  <th className="px-6 py-3 text-sm font-medium text-center">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody>
                {archivedEvents.length > 0 ? (
                  archivedEvents.map((event) => (
                    <tr
                      key={event.id}
                      className="border-b border-gray-300 hover:bg-gray-100 transition bg-white"
                    >
                      <td className="px-6 py-4">{event.title}</td>
                      <td className="px-6 py-4">{event.location}</td>
                      <td className="px-6 py-4">{event.date}</td>
                      <td className="px-6 py-4 flex gap-2 justify-center">
                        <button
                          className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded-md text-sm"
                          onClick={() => handleUnarchive(event.id)}
                        >
                          Restore
                        </button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td
                      colSpan="4"
                      className="px-6 py-8 text-center text-gray-500 bg-white"
                    >
                      No archived events.
                    </td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ManageEvents;
