import React, { useState } from "react";
import { db } from "../firebase/firebaseConfig";
import {
  collection,
  doc,
  setDoc,
  serverTimestamp,
} from "firebase/firestore";
import Sidebar from "../components/Sidebar";
import Swal from "sweetalert2";

function AddEvent() {
  const [title, setTitle] = useState("");
  const [location, setLocation] = useState("");
  const [latitude, setLatitude] = useState("");
  const [longitude, setLongitude] = useState("");
  const [radius, setRadius] = useState("");
  const [date, setDate] = useState("");
  const [description, setDescription] = useState("");

  const handleAddEvent = async (e) => {
    e.preventDefault();

    if (!title || !location || !latitude || !longitude || !radius || !date) {
      Swal.fire({
        icon: "warning",
        title: "Missing Fields",
        text: "Please fill in all required fields before submitting.",
        position: "top-end",
        showConfirmButton: false,
        timer: 1500,
      });
      return;
    }

    try {
      // ðŸ”¥ Generate event doc with fixed ID
      const newEventRef = doc(collection(db, "events"));

      // ðŸ”¥ Create event with that ID
      await setDoc(newEventRef, {
        title,
        location,
        latitude: parseFloat(latitude),
        longitude: parseFloat(longitude),
        radius: parseFloat(radius),
        date,
        description,
        feedbackOpen: false,
        createdAt: serverTimestamp(),
      });

      // ðŸ”¥ Create matching event_questions doc using SAME ID
      await setDoc(doc(db, "event_questions", newEventRef.id), {
        eventId: newEventRef.id,
        questions: [], // empty questions initially
        openEnded: [], // empty open-ended initially
        createdAt: serverTimestamp(),
      });

      Swal.fire({
        icon: "success",
        title: "Event Added!",
        text: "Event & question template created.",
        position: "top-end",
        showConfirmButton: false,
        timer: 1500,
      });

      setTitle("");
      setLocation("");
      setLatitude("");
      setLongitude("");
      setRadius("");
      setDate("");
      setDescription("");
    } catch (error) {
      Swal.fire({
        icon: "error",
        title: "Failed to add event",
        text: error.message,
        position: "top-end",
        showConfirmButton: false,
        timer: 1500,
      });
    }
  };

  return (
    <div className="flex h-screen bg-gray-50 font-poppins">
      <Sidebar />

      <div className="flex-1 flex items-center justify-center p-10 overflow-y-auto">
        <div className="bg-white rounded-xl shadow-lg p-8 w-full max-w-2xl border border-gray-200">
          <h2 className="text-3xl font-semibold text-gray-800 mb-6 text-center">
            Add New Event
          </h2>

          <form onSubmit={handleAddEvent} className="space-y-5">

            {/* All input fields (same as yours, unchanged) */}

            <button
              type="submit"
              className="w-full bg-gray-700 text-white py-3 rounded-md font-semibold hover:bg-gray-600 transition"
            >
              Add Event
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default AddEvent;
